INSERT INTO `mail_template` VALUES (11, 'smtBoardMonitor', '<#macro message code, locale>${loccalMessage.getMessage(code, locale)}</#macro>\r\n<!DOCTYPE html>\r\n<html>\r\n<head>\r\n    <base target=\"_blank\">\r\n    <style type=\"text/css\">\r\n        ::-webkit-scrollbar {\r\n            display: none;\r\n        }\r\n    </style>\r\n    <style type=\"text/css\" id=\"cloudAttachStyle\">\r\n        #divNeteaseBigAttach, #divNeteaseBigAttach_bak {\r\n            display: none;\r\n        }\r\n    </style>\r\n    <style type=\"text/css\">\r\n        body {\r\n            font-size: 10.5pt;\r\n            font-family: arial, verdana, sans-serif;\r\n            line-height: 1.666;\r\n            padding: 0;\r\n            margin: 0;\r\n            overflow: auto;\r\n            white-space: normal;\r\n            word-wrap: break-word;\r\n            min-height: 100px\r\n        }\r\n\r\n        td, input, button, select, body {\r\n            font-family: Helvetica, \'Microsoft Yahei\', verdana\r\n        }\r\n\r\n        pre {\r\n            white-space: pre-wrap;\r\n            white-space: -moz-pre-wrap;\r\n            white-space: -pre-wrap;\r\n            white-space: -o-pre-wrap;\r\n            word-wrap: break-word;\r\n            width: 95%\r\n        }\r\n\r\n        th, td {\r\n            font-family: arial, verdana, sans-serif;\r\n            line-height: 1.666\r\n        }\r\n\r\n        img {\r\n            border: 0\r\n        }\r\n\r\n        header, footer, section, aside, article, nav, hgroup, figure, figcaption {\r\n            display: block\r\n        }\r\n\r\n        .firTab {\r\n            text-indent: 2em\r\n        }\r\n    </style>\r\n\r\n    <style type=\"text/css\" id=\"ntes_link_color\">\r\n        a, td a {\r\n            color: #003399\r\n        }\r\n    </style>\r\n</head>\r\n\r\n\r\n <body>\r\n  <table width=\"80%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\"align=\"center\"style=\"-webkit-font-smoothing: antialiased; font-fami