INSERT INTO `mail_template` (`name`, `content`, `description`, `category`, `modtime`, `createtime`) VALUES('smtRoomManage', '<#macro message code, locale>${loccalMessage.getMessage(code, locale)}</#macro>\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\r\n        \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\"/>\r\n    <title>meeting</title>\r\n</head>\r\n<body style=\"margin:0 auto;	width:100%; font-size:10pt; color:#666666; font-family:\'Microsoft YaHei\',微软雅黑,Arial; \"bgcolor=\"#f2f2f2\">\r\n  <table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"600\" bgcolor=\"#FFFFFF\">\r\n    <tr style=\"height:6px;background:linear-gradient(90deg,#33b8e8 0%,#665ba1 100%);\">\r\n        <td width=\"606px\"></td>\r\n    </tr>\r\n    <tr style=\"height: 103px;background: #ffffff; border: 1px solid #eaeaea;\">\r\n        <td style=\"display: table-cell;vertical-align: middle;width: 130px;height: 39px;\" valign=\"middle\">\r\n            <!-- <img src=\"./CaptainAmerica\" alt=\"logo\" -->\r\n            <!-- style=\"margin-left: 17px; display: block;max-width: 100%;height: 100%;\"> -->\r\n            <img src=\"${logo!\'\'}\" alt=\"logo\"\r\n                style=\"margin-left: 17px; display: block; height: 50px;\">\r\n        </td>\r\n    </tr>\r\n    <tr valign=\"top\">\r\n     <td colspan=\"2\">\r\n      <table align=\"center\" valign=\"top\" cellspacing=\"0\" cellpadding=\"20\" width=\"600\"border=\"0\">\r\n        <tr>\r\n         <td colspan=\"2\">\r\n          <table cellpadding=\"0\" cellspacing=\"0\"style=\" border-bottom:solid 1px; border-color:#c3c3c3;\" width=\"560\">\r\n           <tr>\r\n            <td align=\"left\" style=\"font-size:14pt; line-height:50px;\">\r\n             <span>Meeting Room Message\r\n              </span>\r\n             <span>\r\n                <#if locale == \"zh_TW\" || locale == \"tw\">\r\n                    會議室通知\r\n             	<#else>\r\n             	    会议室通知\r\n             	</#if>\r\n             </span>\r\n            <td align=\"right\">\r\n            </tr>\r\n           </table>\r\n          <br/>\r\n         <span>Dear administrator&nbsp:\r\n          </span>\r\n		  </br>\r\n       <span class=\"k2\">\r\n        <span>You have a meeting room without a meeting room administrator, please deal with it in time.\r\n         </span>\r\n		 <br/><br/>\r\n		 <span>\r\n            <#if locale == \"zh_TW\" || locale == \"tw\">\r\n                尊敬的會議室管理員：\r\n            <#else>\r\n                尊敬的会议室管理员:\r\n            </#if>\r\n         </span>\r\n        <br/>\r\n		 <span class=\"k2\">\r\n        <span>\r\n            <#if locale == \"zh_TW\" || locale == \"tw\">\r\n                 您有會議室缺少會議室管理員，請及時處理。\r\n             <#else>\r\n                 您有会议室缺少会议室管理员，请及时处理。\r\n             </#if>\r\n        </span>\r\n       </span>\r\n      </td>\r\n     </tr>\r\n    <tr valign=\"top\" align=\"center\">\r\n     <td colspan=\"2\">\r\n      <table cellpadding=\"10\" cellspacing=\"0\"style=\"border-bottom:solid 1px #f2f2f2; border-top:1px solid #f2f2f2; border-collapse:collapse;\"width=\"560\">\r\n        <tr>\r\n         <td align=\"right\" style=\"color:#b4b4b4;\" width=\"25%\">\r\n          <span>Meeting Room&nbsp;:\r\n           </span>\r\n		    <br/>\r\n          <span>\r\n           <#if locale == \"en_US\" || locale == \"en\">\r\n            <@message code=\"meeting.repair.message.mail.roomName_zh\" locale=\"${locale}\"/>\r\n           <#else>\r\n            <@message code=\"meeting.repair.message.mail.roomName\" locale=\"${locale}\"/>\r\n           </#if>\r\n          </span>\r\n         </td>\r\n        <td width=\"75%\">${value1!\'\'}\r\n         </td>\r\n        </tr>\r\n   </table>\r\n  </td>\r\n </tr>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr valign=\"top\" align=\"center\">\r\n    <td colspan=\"2\" style=\"padding-bottom:10px;\">\r\n        <table cellpadding=\"10\" cellspacing=\"0\"\r\n               style=\"border-collapse:collapse;background: #FFFFFF;\" width=\"580\">\r\n            <tr>\r\n                <td colspan=\"2\">\r\n                    <span style=\"display:inline-block;margin:25px 0px 25px 60px;font-size:12pt;\">\r\n                        <#if locale == \'zh_CN\' >\r\n                            祝您工作愉快。\r\n                         <#elseif locale ==\'en_US\'>\r\n                            I Wish You a Happy Work.\r\n                         <#else>\r\n                            祝您工作愉快。\r\n                         </#if>\r\n                    </span>\r\n                </td>\r\n            </tr>\r\n        </table>\r\n    </td>\r\n</tr>\r\n<tr style=\"background: #f4f4f4;border: 1px solid #eaeaea;\">\r\n    <td>\r\n        <table width=\"100%\">\r\n            <tr>\r\n                <td width=\"260\"\r\n                    style=\"line-height:150%; color:#333333; font-size:16px;padding-top: 16px;padding-bottom: 16px;padding-left: 10px;\">\r\n                    &nbsp;&nbsp;&nbsp;${systemName!\'\'}<br>\r\n                    &nbsp;&nbsp;&nbsp;<@message code=\"msg.email.content.visitorManagement.phone\" locale=\"${locale}\"/>${phone!\'\'}<br>\r\n                </td>\r\n            </tr>\r\n        </table>\r\n    </td>\r\n</tr>\r\n</table>\r\n</body>\r\n</html>\r\n', '会议室管理员取消邮件', 'smt', '2020-11-20 17:08:40', '2020-11-20 08:38:14');