-- 插入开通服务模板
INSERT INTO `mail_template` (
  `name`,
  `content`,
  `description`,
  `category`
) 
VALUES
  (
    'TenantOpenProductEmail',
    '<#macro message code, locale>${loccalMessage.getMessage(code, locale)}</#macro>\r\n<!DOCTYPE html\r\n    PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta charset=\"utf-8\">\r\n    <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\r\n    <title></title>\r\n    <style>\r\n        * {\r\n            margin: 0;\r\n            padding: 0;\r\n        }\r\n\r\n        img {\r\n            border: 0;\r\n        }\r\n\r\n        .main {\r\n            width: 600px;\r\n            margin: 0 auto;\r\n            background-color: #fff;\r\n            border: 1px solid #dedede;\r\n            font-family: 微软雅黑;\r\n        }\r\n  .header {\r\n     width: 100%;\r\n     height: 98px;\r\n     display: flex;\r\n     justify-content: space-between;\r\n     align-items: center;\r\n   }\r\n   .header > img {\r\n     height: 100%;\r\n     width:100%;\r\n   }\r\n        .container {\r\n            margin: 20px;\r\n        }\r\n\r\n        .page-title {\r\n            border-bottom: #d4d6d5 solid 2px;\r\n            font-size: 20px;\r\n            line-height: 3;\r\n            font-weight: bold\r\n        }\r\n\r\n        .mail-wrap {\r\n            font-size: 16px;\r\n            margin: 25px 0;\r\n        }\r\n\r\n        .mail-container {\r\n            line-height: 2.5;\r\n            width: 100%;\r\n        }\r\n\r\n        .mail-container td {\r\n            padding: 5px 10px;\r\n            color: #57585a;\r\n        }\r\n\r\n        .mail-container td.left {\r\n            width: 120px;\r\n            text-align: right;\r\n            color: #888888;\r\n        }\r\n\r\n        .code {\r\n            text-align: center;\r\n            font-size: 20px;\r\n            font-weight: bold;\r\n            line-height: 1.5;\r\n            color: #55595a;\r\n        }\r\n\r\n        .footer {\r\n            background: #d8d8d8;\r\n            padding: 10px 20px;\r\n        }\r\n\r\n        .link {\r\n            text-decoration: none;\r\n            color: #1212ee;\r\n        }\r\n\r\n        .lh150 {\r\n            line-height: 1.5;\r\n        }\r\n\r\n        .w100 {\r\n            width: 100%;\r\n        }\r\n\r\n        .fb {\r\n            font-weight: bold;\r\n        }\r\n    </style>\r\n</head>\r\n<body>\r\n\r\n<div class=\"main\">\r\n    <div class=\"header\"><img src=\"https://work.virsical.cn/res/bmrLl36Nt0/visit/9f632c1225eb8e41bdccce37a4dba777.jpg\" alt=\"\"></div>\r\n    <div class=\"container\">\r\n        <div class=\"page-title\">${value1!\'\'}</div>\r\n        <div class=\"mail-wrap\">${value10!\'\'}</div>\r\n        <table class=\"mail-container\">\r\n            <tr>\r\n                <td class=\"left\">公司名称：</td>\r\n                <td colspan=\"2\">${value2!\'\'}</td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"left\">开通产品名称：</td>\r\n                <td colspan=\"2\">${value3!\'\'}</td>\r\n            </tr> \r\n            <tr>\r\n                <td class=\"left\">联系人：</td>\r\n                <td colspan=\"2\">${value4!\'\'}</td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"left\">开通时间：</td>\r\n                <td colspan=\"2\">${value5!\'\'}</td>\r\n            </tr>\r\n        </table>\r\n    </div>\r\n    <div class=\"footer\">\r\n        <table class=\"w100\">\r\n            <tr>\r\n                <td class=\"lh150\">威发系统有限公司（<a href=\"https://cloud.virsical.cn\" target=\"_bank\">https://cloud.virsical.cn</a>）\r\n                <br/>售前咨询：400-685-3160<br/>\r\n                </td>\r\n            </tr>\r\n        </table>\r\n    </div>\r\n</div>\r\n</body>\r\n</html>\r\n',
    'TenantOpenProductEmail',
    'oms'
  ) ;
  
