INSERT INTO `mail_template` (`name`, `content`, `description`, `category`, `modtime`, `createtime`, `state`) VALUES
('sws_locker_reservation', '<#macro message code, locale> ${loccalMessage.getMessage(code, locale)} </#macro>\r\n<!DOCTYPE html\r\n        PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\"/>\r\n    <title>Virsical</title>\r\n</head>\r\n\r\n<body>\r\n<table border=\"0\"\r\n       style=\"margin-top: 0;margin-bottom: 0;margin-left: auto;margin-right: auto; width:606px; font-size:16px; color:#333333;background: #f9f9f9; font-family: Helvetica, Tahoma, Arial, \'PingFang SC\', STXihei, \'华文细黑\', \'Microsoft YaHei\', \'微软雅黑\', SimSun, \'宋体\', sans-serif;border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0; \">\r\n    <tr style=\"height:6px;background:linear-gradient(90deg,#33b8e8 0%,#665ba1 100%);\">\r\n        <td width=\"606px\"></td>\r\n    </tr>\r\n    <tr style=\"height: 103px;background: #ffffff; border: 1px solid #eaeaea;\">\r\n        <td style=\"display: table-cell;vertical-align: middle;width: 130px;height: 39px;\">\r\n            <img src=\"${logo!\'\'}\" alt=\"logo\"\r\n                 style=\"margin-left: 17px; display: block;max-width: 100%;height: 100%;\">\r\n        </td>\r\n    </tr>\r\n    <tr>\r\n\r\n        <td style=\"padding-top:16px;padding-bottom: 16px;\">\r\n            <table width=\"573\" align=\"center\"\r\n                   style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;\">\r\n                <tr>\r\n                    <td>\r\n                        <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\r\n                            <tr style=\"font-size: 20px;\">\r\n                              <#if value1 = \"MAIL_LOCKER_NEW\">\r\n                                <td style=\"color:#333333;\">\r\n                                    <@message code=\"mgs.mail.slk.locker.new\" locale=\"${locale}\"/>\r\n                                </td>\r\n                            </#if>\r\n\r\n                            <#if value1 = \"MAIL_LOCKER_NOSIGN\">\r\n                            <td style=\"color:#333333;\">\r\n                                <@message code=\"mgs.mail.slk.locker.nosign\" locale=\"${locale}\"/>\r\n                            </td>\r\n                            </#if>\r\n\r\n                            <#if value1=\"MAIL_LOCKER_FINISH\">\r\n                            <td style=\"color:#333333;\">\r\n                                <@message code=\"mgs.mail.slk.locker.finish\" locale=\"${locale}\"/>\r\n                            </td>\r\n                            </#if>\r\n\r\n                            <#if value1=\"MAIL_LOCKER_RELEASE\">\r\n                            <td style=\"color:#333333;\">\r\n                                <@message code=\"mgs.mail.slk.locker.release\" locale=\"${locale}\"/>\r\n                            </td>\r\n                            </#if>\r\n                            </tr>\r\n                            <tr style=\"font-size: 16px;line-height: 2;\">\r\n                                <td style=\"color:#333333; padding-top:25px\";>\r\n                                    <@message code=\"mgs.mail.sws.hello\" locale=\"${locale}\"/> ，${value2!\'\'}:<br>\r\n                                    <div style =\" word-break: break-all ; word-wrap:break-word;\">\r\n                                    &nbsp;&nbsp;&nbsp;&nbsp;\r\n                                    <#if value1 = \"MAIL_LOCKER_NEW\">\r\n                                        <@message code=\"mgs.mail.slk.locker.num\" locale=\"${locale}\"/>\r\n                                        ${value14!\'\'}，<@message code=\"mgs.mail.sws.station.content.ordinary\" locale=\"${locale}\"/>\r\n                                       </div>\r\n                                    </#if>\r\n\r\n                                    <#if value1 = \"MAIL_LOCKER_NOSIGN\">\r\n                                        <@message code=\"mgs.mail.slk.locker.call\" locale=\"${locale}\"/>\r\n                                        ${value14!\'\'}，\r\n                                        <@message code=\"mgs.mail.sws.station.content4\" locale=\"${locale}\"/>\r\n                                    </#if>\r\n\r\n                                    <#if value1 = \"MAIL_LOCKER_FINISH\">\r\n                                        <@message code=\"mgs.mail.slk.locker.call\" locale=\"${locale}\"/>\r\n                                        ${value14!\'\'}，\r\n                                        <@message code=\"mgs.mail.sws.station.content5\" locale=\"${locale}\"/>\r\n                                         ${value9!\'\'}\r\n                                         <@message code=\"mgs.mail.sws.station.admin.cancel\" locale=\"${locale}\"/>\r\n                                    </#if>\r\n\r\n                                    <#if value1 = \"MAIL_LOCKER_RELEASE\">\r\n                                       <@message code=\"mgs.mail.sws.station.tit\" locale=\"${locale}\"/>\r\n                                       ${value14!\'\'}，\r\n                                       <@message code=\"mgs.mail.sws.station.cancel\" locale=\"${locale}\"/>\r\n                                    </#if>\r\n\r\n				                    <#if value1 = \"RESERVATION_NOSIGN\">\r\n                                        <@message code=\"mgs.mail.slk.locker.call\" locale=\"${locale}\"/>\r\n                                         ${value14!\'\'}\r\n                                         <@message code=\"mgs.mail.sws.station.content4\" locale=\"${locale}\"/>\r\n                                    </#if>\r\n                                </td>\r\n                            </tr>\r\n                        </table>\r\n                    </td>\r\n                </tr>\r\n                <tr style=\"border-top: 1px solid  #eaeaea;\">\r\n                    <td>\r\n                        <table width=\"100%\" bgcolor=\"#FFFFFF\">\r\n                        <#if value13=\"2\">\r\n                           ${value3!\'\'}\r\n                        </#if>\r\n                        <#if value13=\"1\">\r\n                            <tr>\r\n                                <td width=\"150\" height=\"50\"\r\n                                    style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%;padding-top:25px; \">\r\n                                    <@message code=\"mgs.mail.slk.locker.num\" locale=\"${locale}\"/> :\r\n                                <td style=\"font-size: 16px; color:#333333; text-align:left;padding-top: 25px; padding-left: 15px;word-break: break-all ; word-wrap:break-word;\">\r\n                                     <div style=\"line-height:25px;\">${value3!\'\'} </div>\r\n                                </td>\r\n                            </tr>\r\n                            <tr>\r\n                                <td width=\"150\" height=\"50\"\r\n                                    style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n                                    <@message code=\"mgs.email.station.address\" locale=\"${locale}\"/> :\r\n                                <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 15px;word-break: break-all ; word-wrap:break-word;\">\r\n                                    ${value4!\'\'}\r\n                                </td>\r\n                            </tr>\r\n\r\n                            <tr>\r\n                                <td width=\"150\" height=\"50\"\r\n                                    style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n                                    <@message code=\"mgs.email.station.starttime\" locale=\"${locale}\"/>:\r\n                                <td style=\"font-size: 16px; color:#333333; text-align:left; padding-left: 15px;word-break: break-all ; word-wrap:break-word;\">\r\n                                    ${value5!\'\'}\r\n                                </td>\r\n                            </tr>\r\n                            <tr>\r\n                                <td width=\"150\" height=\"50\"\r\n                                    style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n                                    <@message code=\"mgs.mail.sws.station.entime\" locale=\"${locale}\"/>:\r\n                                <td style=\"font-size: 16px; color:#333333; text-align:left; padding-left: 15px;word-break: break-all ; word-wrap:break-word;\">\r\n                                   ${value6!\'\'}\r\n                                </td>\r\n                            </tr>\r\n                            </#if>\r\n                        </table>\r\n                    </td>\r\n                </tr>\r\n\r\n                <tr style=\"border-top: 1px solid #eaeaea;\">\r\n                    <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\r\n                        <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">\r\n                            <@message code=\"mgs.mail.sws.remarks\" locale=\"${locale}\"/>\r\n                        </p>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n    <tr style=\"background: #f4f4f4;border: 1px solid #eaeaea;\">\r\n        <td>\r\n            <table width=\"100%\">\r\n                <tr>\r\n                    <td width=\"260\"\r\n                        style=\"line-height:150%; color:#333333; font-size:16px;padding-top: 16px;padding-bottom: 16px;padding-left: 10px;\">\r\n                        &nbsp;&nbsp;&nbsp;<@message code=\"mgs.mail.sws.systemname\" locale=\"${locale}\"/>\r\n                        <br>\r\n                        &nbsp;&nbsp;&nbsp;<@message code=\"mgs.mail.sws.phone\" locale=\"${locale}\"/> ：<@message code=\"mgs.mail.sws.phone.no\" locale=\"${locale}\"/><br>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n</table>\r\n</body>\r\n\r\n</html>', '储物柜邮件通知', 'sws', '2020-12-28 01:16:18', '2020-12-25 05:30:21', 0);
