INSERT INTO `mail_template` (`name`, `content`, `description`, `category`, `modtime`, `createtime`) VALUES('smtBoardMonitor','<#macro message code, locale>${loccalMessage.getMessage(code, locale)}</#macro>\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\r\n        \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\"/>\r\n    <title>meeting reminder</title>\r\n</head>\r\n\r\n<body style=\"margin:0 auto; width:100%; font-size:10pt; color:#666666; font-family:\'Microsoft YaHei\',微软雅黑,Arial; \"bgcolor=\"#f2f2f2\">\r\n  <table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"600\" bgcolor=\"#FFFFFF\">\r\n       <tr style=\"height:6px;background:linear-gradient(90deg,#33b8e8 0%,#665ba1 100%);\">\r\n           <td width=\"606px\"></td>\r\n       </tr>\r\n       <tr style=\"height: 103px;background: #ffffff; border: 1px solid #eaeaea;\">\r\n           <td style=\"display: table-cell;vertical-align: middle;width: 130px;height: 39px;\" valign=\"middle\">\r\n               <!-- <img src=\"./CaptainAmerica\" alt=\"logo\" -->\r\n               <!-- style=\"margin-left: 17px; display: block;max-width: 100%;height: 100%;\"> -->\r\n               <img src=\"${logo!\'\'}\" alt=\"logo\"\r\n                   style=\"margin-left: 17px; display: block; height: 50px;\">\r\n           </td>\r\n       </tr>\r\n</table>\r\n<tr>\r\n<td colspan=\"2\">\r\n<table align=\"center\" class=\"banner\" cellspacing=\"0\" cellpadding=\"0\" width=\"600\"height=\"100\">\r\n<tr>\r\n<td colspan=\"2\" bgcolor=\"#F2F2F2\">\r\n <table cellpadding=\"0\" cellspacing=\"0\" style=\"margin:10px;\" width=\"580\"bgcolor=\"#FFFFFF\">\r\n<tr>\r\n<td align=\"left\" style=\"font-size:12pt; line-height:30px;\">\r\n<div style=\"font-size: 16px; padding: 10px; color: #333333; line-height: 1.6;\">\r\n<div>\r\n<span>\r\n<@message code=\"msg.email.monitor.hi\" locale=\"${locale}\"/>\r\n</span>\r\n<p class=\"firTab\" style=\"text-indent:30px\">\r\n<@message code=\"msg.email.monitor.meeting\" locale=\"${locale}\"/>${value1!\'\'}\r\n<@message code=\"msg.email.monitor.board\" locale=\"${locale}\"/>${value2!\'\'}\r\n<@message code=\"msg.email.monitor.time\" locale=\"${locale}\"/>${value3!\'\'}\r\n<#if value4==\'2\'>\r\n<@message code=\"msg.email.monitor.error\" locale=\"${locale}\"/>\r\n<@message code=\"msg.email.monitor.app\" locale=\"${locale}\"/>\r\n</p>\r\n<#elseif value4==\'3\'>\r\n<@message code=\"msg.email.monitor.error\" locale=\"${locale}\"/>\r\n<@message code=\"msg.email.monitor.web\" locale=\"${locale}\"/>\r\n</p>\r\n<#else>\r\n<@message code=\"msg.email.monitor.aller\" locale=\"${locale}\"/>\r\n</p>\r\n</#if>\r\n<p class=\"firTab\" style=\"text-indent:30px\">\r\n<@message code=\"msg.email.monitor.warn\" locale=\"${locale}\"/>\r\n</p>\r\n</div>\r\n</div>\r\n</td>\r\n</tr>\r\n</table>\r\n</td>\r\n</tr>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr valign=\"top\">\r\n<td colspan=\"2\" bgcolor=\"#F2F2F2\">\r\n<table align=\"center\" valign=\"top\" cellspacing=\"0\" cellpadding=\"0\" width=\"580\"border=\"0\" bgcolor=\"#FFFFFF\">\r\n<tr valign=\"top\" align=\"center\">\r\n    <td colspan=\"2\" style=\"padding-bottom:10px;\">\r\n        <table cellpadding=\"10\" cellspacing=\"0\"\r\n               style=\"border-collapse:collapse;background: #FFFFFF;\" width=\"580\">\r\n            <tr>\r\n                <td colspan=\"2\">\r\n                    <span style=\"display:inline-block;margin:25px 0px 25px 60px;font-size:12pt;\">\r\n                        <#if locale == \'zh_CN\' >\r\n                            祝您工作愉快。\r\n                         <#elseif locale ==\'en_US\'>\r\n                            I Wish You a Happy Work.\r\n                         <#else>\r\n                            祝您工作愉快。\r\n                         </#if>\r\n                    </span>\r\n                </td>\r\n            </tr>\r\n        </table>\r\n    </td>\r\n</tr>\r\n<tr style=\"background: #f4f4f4;border: 1px solid #eaeaea;\">\r\n    <td>\r\n        <table width=\"100%\">\r\n            <tr>\r\n                <td width=\"260\"\r\n                    style=\"line-height:150%; color:#333333; font-size:16px;padding-top: 16px;padding-bottom: 16px;padding-left: 10px;\">\r\n                    &nbsp;&nbsp;&nbsp;${systemName!\'\'}<br>\r\n                    &nbsp;&nbsp;&nbsp;<@message code=\"msg.email.content.visitorManagement.phone\" locale=\"${locale}\"/>${phone!\'\'}<br>\r\n                </td>\r\n            </tr>\r\n        </table>\r\n    </td>\r\n</tr>\r\n</table>\r\n</body>\r\n</html>\r\n','展板状态异常邮件','smt','2019-12-09 16:48:44','2019-10-16 08:38:13');
INSERT INTO `mail_template` (`name`, `content`, `description`, `category`, `modtime`, `createtime`) VALUES('smtMeeting','<#macro message code, locale>${loccalMessage.getMessage(code, locale)}</#macro>\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\r\n  \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n  <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\"/>\r\n  <title>meeting reminder</title>\r\n</head>\r\n\r\n<body>\r\n  <table border=\"0\" style=\"margin-top: 0;margin-bottom: 0;margin-left: auto;margin-right: auto; width:606px; font-size:16px; color:#333333;background: #f9f9f9; font-family: Helvetica, Tahoma, Arial, \'PingFang SC\', STXihei, \'华文细黑\', \'Microsoft YaHei\', \'微软雅黑\', SimSun, \'宋体\', sans-serif;border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0; >\r\n   <tr style=\"height:6px;background:linear-gradient(90deg,#33b8e8 0%,#665ba1 100%);\">\r\n       <td style=\"height:6px;background:linear-gradient(90deg,#33b8e8 0%,#665ba1 100%);\" width=\"606px\"></td>\r\n   </tr>\r\n   <tr style=\"height: 103px;background: #ffffff; border: 1px solid #eaeaea;\">\r\n       <td style=\"display: table-cell;vertical-align: middle;width: 130px;height: 39px;\" valign=\"middle\">\r\n           <div style=\"width: 560px; height: 70px; margin: auto\">\r\n             <img src=\"${logo!\'\'}\" alt=\"logo\" style=\"display: block; height: 70px;\">\r\n           </div>\r\n       </td>\r\n   </tr>\r\n <tr>\r\n  <td colspan=\"2\">\r\n   <table align=\"center\" class=\"banner\" cellspacing=\"0\" cellpadding=\"0\" style=\"width: 600px;height: 100px\">\r\n     <tr>\r\n      <td colspan=\"2\" bgcolor=\"#EFEFEF\">\r\n       <table cellpadding=\"0\" cellspacing=\"0\" style=\"margin:10px; width: 580px; background-color: #FFFFFF\">\r\n        <tr>\r\n         <td align=\"left\" colspan=\"2\"style=\"font-size:20px; line-height:30px;\">\r\n          <span style=\"margin-left:10px;display: block;font-family: PingFang SC;font-weight: 500;color: rgba(51,51,51,1);\">\r\n           <#if value5==\'1\'>\r\n            <span style=\"display: block;\">Invited of Meeting\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_invite_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_invite\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'2\'>\r\n            <span style=\"display: block\">Updated of Meeting\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_update_invite_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_update_invite\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'3\' || value5==\'10\'>\r\n            <span style=\"display: block\">Canceled of Meeting\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_cancel_appoint_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_cancel_appoint\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'4\'>\r\n            <span style=\"display: block\">Reminder of Meeting\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_reminder_appoint_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_reminder_appoint\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'6\' || value5==\'7\'>\r\n            <span style=\"display: block\">ACK of Meeting\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_ack_appoint_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_ack_appoint\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'8\'>\r\n            <span style=\"display: block\">Meeting scheduled successfully\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_create_appoint_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_create_appoint\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'9\'>\r\n            <span style=\"display: block\">Updated of Meeting\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_update_appoint_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_update_appoint\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'15\' || value5==\'23\'>\r\n            <span style=\"display: block\">Infrared Device Reminding\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.alarm.tile_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.alarm.tile\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'18\'>\r\n            <span style=\"display: block\">Reminder of Meeting\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.willend.tile_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.willend.tile\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'5\' || value5==\'11\' || value5==\'12\' || value5==\'13\' || value5==\'40\' || value5==\'41\'>\r\n            <span style=\"display: block\">Notice of Meeting</span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_appoint_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_appoint\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'66\'>\r\n            <span style=\"display: block\">Review Message\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_audit_title_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_audit_title\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'77\' || value5==\'78\' || value5==\'88\' || value5==\'99\'>\r\n            <span style=\"display: block\">Meeting service notice\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.meeting_svc_notice_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.meeting_svc_notice\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'101\' || value5==\'102\'>\r\n            <span style=\"display: block\">Room maintain notice\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.maintain_notice_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.maintain_notice\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'103\' || value5==\'104\'>\r\n            <span style=\"display: block\">Room status notice\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.visible_notice_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.visible_notice\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           <#elseif value5==\'20\' || value5==\'21\' || value5==\'22\'>\r\n            <span style=\"display: block\">Meeting Room Automatic Release\r\n             </span>\r\n            <span style=\"display: block\">\r\n             <#if locale == \"en_US\" || locale == \"en\">\r\n              <@message code=\"meeting.remind.message.mail.emailCancel.alarm.tile_zh\" locale=\"${locale}\"/>\r\n             <#else>\r\n              <@message code=\"meeting.remind.message.mail.emailCancel.alarm.tile\" locale=\"${locale}\"/>\r\n             </#if>\r\n            </span>\r\n           </#if>\r\n          </span>\r\n         </td>\r\n        </tr>\r\n       <tr>\r\n        <td colspan=\"2\" align=\"left\"style=\"font-size:16px; line-height:30px;color: rgba(51,51,51,1);\">\r\n         <#if value5!=\'15\' && value5!=\'17\' && value5!=\'20\' && value5!=\'21\'>\r\n          <span style=\"margin-left:10px;\">\r\n           <@message code=\"msg.email.content.invitUseSys.title.prefix.en\" locale=\"${locale}\"/>${callEn!\'\'},\r\n         </span>\r\n        </#if>\r\n       </td>\r\n      </tr>\r\n     <tr>\r\n      <td align=\"left\" style=\"font-size: 16px; line-height: 30px;color: rgba(51,51,51,1);\">\r\n       <p class=\"k2\"style=\"margin-left: 10px; margin-right: 10px; margin-top: 0px; margin-bottom: 10px; word-break: break-all;\" width=\"80%\">\r\n        <#if value5==\'1\'>\r\n        ${createUserEn!\'\'} booked a meeting, which will start in ${meetingTimeEn!\'\'}&#44;\r\n        <@message code=\"meeting.remind.message.mail.meeting.create.succ.in.en\" locale=\"${locale}\"/>${value34?split(\",\")[0]!\'\'}\r\n       <@message code=\"meeting.remind.message.mail.meeting.create.succ.open.en\" locale=\"${locale}\"/>\r\n      <#elseif value5==\'2\'>\r\n        Your meeting of [${value8!\'\'}] has been modified.\r\n     <#elseif value5==\'3\'>\r\n      <#if value28==\'0\'>\r\n        Your meeting of [${value8!\'\'}] has been canceled.\r\n     <#elseif value28==\'1\'>\r\n      <@message code=\"meeting.remind.message.mail.common.your.en\" locale=\"${locale}\"/>[${value8!\'\'}]\r\n     <@message code=\"meeting.remind.message.mail.meeting.has.not.sign.en\" locale=\"${locale}\"/>${value34?split(\",\")[0]!\'\'}\r\n    <@message code=\"meeting.remind.message.mail.meeting.release.succ.notice.en\" locale=\"${locale}\"/>\r\n   <#elseif value28==\'