/*新增展板告警汇总邮件模板 */
INSERT INTO `mail_template` (`id`, `name`, `content`, `description`, `category`, `modtime`, `createtime`, `state`) VALUES
(262, 'smtBoardMonitorStatistics_zh_CN', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"600\" bgcolor=\"#FFFFFF\">\r\n    <tr>\r\n        <td colspan=\"2\">\r\n            <table align=\"center\" class=\"banner\" cellspacing=\"0\" cellpadding=\"0\" width=\"600\"height=\"100\">\r\n                <tr>\r\n                    <td colspan=\"2\" bgcolor=\"#F2F2F2\">\r\n                        <table cellpadding=\"0\" cellspacing=\"0\" style=\"padding:10px;\" bgcolor=\"#FFFFFF\">\r\n                            <tr>\r\n                                <td align=\"left\" style=\"font-size:12pt; line-height:30px;\">\r\n                                    <div style=\"font-size: 16px; color: #333333; line-height: 1.6;\">\r\n                                        <div>\r\n                                            <span>会议室管理员，您好 :</span>\r\n                                            <p class=\"firTab\" style=\"text-indent:30px;word-wrap:break-word;word-break:break-all;\">\r\n                                                过去${meetingTime!\'\'}小时内，有${doorNumber!\'\'}个展板设备发生故障，\r\n                                                详情查看：<a href=\"${doorUrl!\'\'}\" >${doorUrl!\'\'}</a>\r\n                                            </p>\r\n                                        </div>\r\n                                    </div>\r\n                                </td>\r\n                            </tr>\r\n                        </table>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n</table>\r\n', '会议告警汇总邮件-简体中文', 'smt', '2021-11-11 09:52:24', '2021-11-11 07:33:18', 0),
(263, 'smtBoardMonitorStatistics_zh_TW', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"600\" bgcolor=\"#FFFFFF\">\r\n    <tr>\r\n        <td colspan=\"2\">\r\n            <table align=\"center\" class=\"banner\" cellspacing=\"0\" cellpadding=\"0\" width=\"600\"height=\"100\">\r\n                <tr>\r\n                    <td colspan=\"2\" bgcolor=\"#F2F2F2\">\r\n                        <table cellpadding=\"0\" cellspacing=\"0\" style=\"padding:10px;\" bgcolor=\"#FFFFFF\">\r\n                            <tr>\r\n                                <td align=\"left\" style=\"font-size:12pt; line-height:30px;\">\r\n                                    <div style=\"font-size: 16px; color: #333333; line-height: 1.6;\">\r\n                                        <div>\r\n                                            <span>會議室管理員，您好 :</span>\r\n                                            <p class=\"firTab\" style=\"text-indent:30px;word-wrap:break-word;word-break:break-all;\">\r\n                                                過去${meetingTime!\'\'}小時內，有${doorNumber!\'\'}個展板設備發生故障，\r\n                                                詳情查看：<a href=\"${doorUrl!\'\'}\" >${doorUrl!\'\'}</a>\r\n                                            </p>\r\n                                        </div>\r\n                                    </div>\r\n                                </td>\r\n                            </tr>\r\n                        </table>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n</table>\r\n', '会议告警汇总邮件-繁体中文', 'smt', '2021-11-11 09:52:14', '2021-11-11 09:10:59', 0),
(264, 'smtBoardMonitorStatistics_en', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"600\" bgcolor=\"#FFFFFF\">\r\n    <tr>\r\n        <td colspan=\"2\">\r\n            <table align=\"center\" class=\"banner\" cellspacing=\"0\" cellpadding=\"0\" width=\"600\"height=\"100\">\r\n                <tr>\r\n                    <td colspan=\"2\" bgcolor=\"#F2F2F2\">\r\n                        <table cellpadding=\"0\" cellspacing=\"0\" style=\"padding:10px;\" bgcolor=\"#FFFFFF\">\r\n                            <tr>\r\n                                <td align=\"left\" style=\"font-size:12pt; line-height:30px;\">\r\n                                    <div style=\"font-size: 16px; color: #333333; line-height: 1.6;\">\r\n                                        <div>\r\n                                            <span>Hi，Administrator :</span>\r\n                                            <p class=\"firTab\" style=\"text-indent:30px;word-wrap:break-word;word-break:break-all;\">\r\n                                                In the last ${meetingTime!\'\'}hours，there were ${doorNumber!\'\'} board devices had failed，\r\n                                                view details：<a href=\"${doorUrl!\'\'}\" >${doorUrl!\'\'}</a>\r\n                                            </p>\r\n                                        </div>\r\n                                    </div>\r\n                                </td>\r\n                            </tr>\r\n                        </table>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n</table>\r\n', '会议告警汇总邮件-英文', 'smt', '2021-11-11 09:52:03', '2021-11-11 09:11:26', 0);

/* 修改展板告警邮件模板 */
UPDATE `mail_template` SET `content` = '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"600\" bgcolor=\"#FFFFFF\">\r\n    <tr>\r\n        <td colspan=\"2\">\r\n            <table align=\"center\" class=\"banner\" cellspacing=\"0\" cellpadding=\"0\" width=\"600\"height=\"100\">\r\n                <tr>\r\n                    <td colspan=\"2\" bgcolor=\"#F2F2F2\">\r\n                        <table cellpadding=\"0\" cellspacing=\"0\" style=\"padding:10px;\" bgcolor=\"#FFFFFF\">\r\n                            <tr>\r\n                                <td align=\"left\" style=\"font-size:12pt; line-height:30px;\">\r\n                                    <div style=\"font-size: 16px; color: #333333; line-height: 1.6;\">\r\n                                        <div>\r\n                                            <span>会议室管理员，您好 :</span>\r\n                                            <p class=\"firTab\" style=\"text-indent:30px\">\r\n                                                ${roomName!\'\'}会议室的展板(设备ID: ${boardDeviceId!\'\'})，于${meetingTime!\'\'}\r\n                                                <#if emailType?? && emailType==\'2\'>\r\n                                                    （GMT+8），发生APP异常。\r\n                                                <#elseif emailType?? && emailType==\'3\'>\r\n                                                    （GMT+8），发生页面异常。\r\n                                                <#else>\r\n                                                    （GMT+8），发生网络异常。\r\n                                                </#if>\r\n                                            </p>\r\n                                            <p class=\"firTab\" style=\"text-indent:30px\">\r\n                                                请通过会议管理端进行故障修复。\r\n                                            </p>\r\n                                        </div>\r\n                                    </div>\r\n                                </td>\r\n                            </tr>\r\n                        </table>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n</table>\r\n' WHERE `name` = 'smtBoardMonitor_zh_CN';
UPDATE `mail_template` SET `content` = '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"600\" bgcolor=\"#FFFFFF\">\r\n    <tr>\r\n        <td colspan=\"2\">\r\n            <table align=\"center\" class=\"banner\" cellspacing=\"0\" cellpadding=\"0\" width=\"600\"height=\"100\">\r\n                <tr>\r\n                    <td colspan=\"2\" bgcolor=\"#F2F2F2\">\r\n                        <table cellpadding=\"0\" cellspacing=\"0\" style=\"padding:10px;\" bgcolor=\"#FFFFFF\">\r\n                            <tr>\r\n                                <td align=\"left\" style=\"font-size:12pt; line-height:30px;\">\r\n                                    <div style=\"font-size: 16px; color: #333333; line-height: 1.6;\">\r\n                                        <div>\r\n                                            <span>會議室管理員，您好 :</span>\r\n                                            <p class=\"firTab\" style=\"text-indent:30px\">\r\n                                                ${roomName!\'\'}會議室的展板(設備ID: ${boardDeviceId!\'\'})，於${meetingTime!\'\'}\r\n                                                <#if emailType?? && emailType==\'2\'>\r\n                                                    （GMT+8），發生APP異常。\r\n                                                <#elseif emailType?? && emailType==\'3\'>\r\n                                                    （GMT+8），發生頁面異常。\r\n                                                <#else>\r\n                                                    （GMT+8），發生網絡異常。\r\n                                                </#if>\r\n                                            </p>\r\n                                            <p class=\"firTab\" style=\"text-indent:30px\">\r\n                                                請通過會議管理端進行故障修復。\r\n                                            </p>\r\n                                        </div>\r\n                                    </div>\r\n                                </td>\r\n                            </tr>\r\n                        </table>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n</table>\r\n' WHERE `name` = 'smtBoardMonitor_zh_TW';
UPDATE `mail_template` SET `content` = '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"600\" bgcolor=\"#FFFFFF\">\r\n    <tr>\r\n        <td colspan=\"2\">\r\n            <table align=\"center\" class=\"banner\" cellspacing=\"0\" cellpadding=\"0\" width=\"600\"height=\"100\">\r\n                <tr>\r\n                    <td colspan=\"2\" bgcolor=\"#F2F2F2\">\r\n                        <table cellpadding=\"0\" cellspacing=\"0\" style=\"padding:10px;\" bgcolor=\"#FFFFFF\">\r\n                            <tr>\r\n                                <td align=\"left\" style=\"font-size:12pt; line-height:30px;\">\r\n                                    <div style=\"font-size: 16px; color: #333333; line-height: 1.6;\">\r\n                                        <div>\r\n                                            <span>Hello, Administrator :</span>\r\n                                            <p class=\"firTab\" style=\"text-indent:30px\">\r\n                                                ${roomName!\'\'} exhibition board (Device ID: ${boardDeviceId!\'\'})，at ${meetingTime!\'\'}\r\n                                                <#if emailType?? && emailType==\'2\'>\r\n                                                    (GMT+8), occur APP is error.\r\n                                                <#elseif emailType?? && emailType==\'3\'>\r\n                                                    (GMT+8), occur Web is error.\r\n                                                <#else>\r\n                                                    (GMT+8), occur Network exception.\r\n                                                </#if>\r\n                                            </p>\r\n                                            <p class=\"firTab\" style=\"text-indent:30px\">\r\n                                                Please repair the fault through the meeting Manager.\r\n                                            </p>\r\n                                        </div>\r\n                                    </div>\r\n                                </td>\r\n                            </tr>\r\n                        </table>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n</table>\r\n' WHERE `name` = 'smtBoardMonitor_en';