-- 邮件模板改用Freemarker.
update `mail_template` set `content` = '<table width=\"100%\" bgcolor=\"#FFFFFF\"\r\nstyle=\"padding-left: 18px; padding-top: 21px;\">\r\n<tr style=\"font-size: 20px;\">\r\n<td style=\"color: #333333;\">人脸审核通知</td>\r\n<td style=\"width: 100px;\"></td>\r\n</tr>\r\n<tr style=\"font-size: 16px;\">\r\n<td style=\"color: #333333; padding-top: 15px;\">管理员，您好：</td>\r\n<td style=\"width: 100px;\"></td>\r\n</tr>\r\n<tr style=\"font-size: 16px;\">\r\n<td\r\nstyle=\"color: #333333; padding-top: 15px; padding-bottom: 15px;\">\r\n以下用户上传了新的人脸图片，有待审核：<br />\r\n<br />\r\n用户姓名：${value1!\'\'}<br />\r\n用户账号：${value2!\'\'}<br />\r\n<br />\r\n请<a href=\"${value3!\'\'}\"\r\nstyle=\"color: #0d75cd; text-decoration: none;\">\r\n点击这里 </a>，登录管理后台，进入 权限管理 >> 人脸库 菜单进行审核。<br/>\r\n</td>\r\n</tr>\r\n</table>' where `name` = 'sasFaceAudit';
update `mail_template` set `content` = '<table width=\"100%\" bgcolor=\"#FFFFFF\"\r\nstyle=\"padding-left: 18px; padding-top: 21px;\">\r\n<tr style=\"font-size: 20px;\">\r\n<td style=\"color: #333333;\">人臉稽核通知</td>\r\n<td style=\"width: 100px;\"></td>\r\n</tr>\r\n<tr style=\"font-size: 16px;\">\r\n<td style=\"color: #333333; padding-top: 15px;\">管理員，您好：</td>\r\n<td style=\"width: 100px;\"></td>\r\n</tr>\r\n<tr style=\"font-size: 16px;\">\r\n<td\r\nstyle=\"color: #333333; padding-top: 15px; padding-bottom: 15px;\">\r\n以下用戶上傳了新的人臉圖片，有待稽核：<br />\r\n<br />\r\n用戶姓名：${value1!\'\'}<br />\r\n用戶帳號：${value2!\'\'}<br />\r\n<br />\r\n請<a href=\"${value3!\'\'}\"\r\nstyle=\"color: #0d75cd; text-decoration: none;\">\r\n點擊這裡 </a>，登入管理後臺，進入許可權管理>>人臉庫選單進行稽核。<br/>\r\n</td>\r\n</tr>\r\n</table>' where `name` = 'sasFaceAudit_zh_TW';
update `mail_template` set `content` = '<table width=\"100%\" bgcolor=\"#FFFFFF\"\r\nstyle=\"padding-left: 18px; padding-top: 21px;\">\r\n<tr style=\"font-size: 20px;\">\r\n<td style=\"color: #333333;\">Face audit notification</td>\r\n<td style=\"width: 100px;\"></td>\r\n</tr>\r\n<tr style=\"font-size: 16px;\">\r\n<td style=\"color: #333333; padding-top: 15px;\">Hello, Administrator:</td>\r\n<td style=\"width: 100px;\"></td>\r\n</tr>\r\n<tr style=\"font-size: 16px;\">\r\n<td\r\nstyle=\"color: #333333; padding-top: 15px; padding-bottom: 15px;\">\r\nThe following user has uploaded new face images to be reviewed:<br />\r\n<br />\r\nUser name：${value1!\'\'}<br />\r\nUser account：${value2!\'\'}<br />\r\n<br />\r\nPlease <a href=\"${value3!\'\'}\"\r\nstyle=\"color: #0d75cd; text-decoration: none;\">\r\nclick here </a> to log in the management backgroud and enter the \"Permission Management > > Face Library Management\" menu for audit.<br/>\r\n</td>\r\n</tr>\r\n</table>' where `name` = 'sasFaceAudit_en';
