INSERT INTO `mail_template`(`name`, `content`, `description`, `category`, `modtime`, `createtime`, `state`) VALUES ('smtMeeting_zh_CN', '<table align=\"center\" border=\"0\" style=\"margin-top: -16px;margin-bottom: -16px; margin-left: auto;margin-right: auto; font-size:16px; color:#333333;background: #f9f9f9; font-family: Helvetica, Tahoma, Arial, \'PingFang SC\', STXihei, \'华文细黑\', \'Microsoft YaHei\', \'微软雅黑\', SimSun, \'宋体\', sans-serif; border-collapse: collapse; border-spacing: 0;\" >\n    <tr>\n        <td bgcolor=\"#EFEFEF\">\n           <table cellpadding=\"0\" cellspacing=\"0\" style=\"margin:10px; pading: 5px; width: 580px; background-color: #FFFFFF\">\n                <tr>\n                     <td align=\"left\" colspan=\"2\"style=\"font-size:20px; line-height:30px;\">\n                        <div style=\"margin-left:10px;display: block;font-family: PingFang SC;font-weight: 500;color: rgba(51,51,51,1);\">\n                            <#if emailType??>\n                                <#if emailType==\'1\'>\n                                    <div>参会通知</div>\n                                <#elseif emailType==\'2\'>\n                                    <div>会议修改通知</div>\n                                <#elseif emailType==\'3\' || emailType==\'10\'>\n                                    <div>会议取消通知</div>\n                                <#elseif emailType==\'4\'>\n                                    <div>会议提醒通知</div>\n                                <#elseif emailType==\'6\' || emailType==\'7\'>\n                                    <div>会议回执通知</div>\n                                <#elseif emailType==\'8\'>\n                                    <div>会议创建通知</div>\n                                <#elseif emailType==\'9\'>\n                                    <div>会议修改通知</div>\n                                <#elseif emailType==\'15\' || emailType==\'22\' || emailType==\'23\'>\n                                    <div>红外感知设备提醒</div>\n                    