INSERT INTO `mail_template` (`name`, `content`, `description`, `category`, `modtime`, `createtime`, `state`) VALUES
('cloudSubscribeSuccessEmail_en', '<style>\r\n    .content * {\r\n        margin: 0;\r\n        padding: 0;\r\n    }\r\n    .content {\r\n        width: 610px;\r\n        margin: 0 auto;\r\n        font-size: 16px;\r\n        position: relative;\r\n    }\r\n    .line {\r\n        width: 100%;\r\n        height: 5px;\r\n        background: linear-gradient(243deg, #665ba1 0%, #33b8e8 100%);\r\n    }\r\n    .header {\r\n        width: 100%;\r\n        height: 100px;\r\n        position: relative;\r\n        border-bottom: 2px solid #f5f5f5;\r\n        background: #fff;\r\n    }\r\n    .header .logo {\r\n        height: 50px;\r\n        width: 50px;\r\n        position: absolute;\r\n        left: 20px;\r\n        top: 50%;\r\n        margin-top: -25px;\r\n    }\r\n    .header .title {\r\n        font-size: 25px;\r\n        color: #191515;\r\n        font-weight: bold;\r\n        position: absolute;\r\n        left: 88px;\r\n        top: 50%;\r\n        margin-top: -15px;\r\n    }\r\n    .footer {\r\n        width: 100%;\r\n        height: 170px;\r\n        box-sizing: border-box;\r\n        background-color: #fafafa;\r\n        padding-top: 30px;\r\n        padding-left: 25px;\r\n    }\r\n    .footer .logo {\r\n        width: 35px;\r\n        height: 30px;\r\n    }\r\n    .footer .name {\r\n        margin-top: 26px;\r\n        color: #282828;\r\n        font-size: 16px;\r\n    }\r\n    .footer .tel {\r\n        margin-top: 10px;\r\n        color: #282828;\r\n        font-size: 16px;\r\n    }\r\n\r\n    .body {\r\n        padding: 25px 40px;\r\n        color: #282828;\r\n        background-color: #fff;\r\n        font-size: 16px;\r\n    }\r\n\r\n    .body-head {\r\n        margin: 25px 0;\r\n        color: #282828;\r\n        line-height: 22px;\r\n    }\r\n    .price {\r\n        color: #ff5c00;\r\n    }\r\n    .table {\r\n        width: 100%;\r\n    }\r\n    .table tr {\r\n        height: 45px;\r\n    }\r\n    .table td:first-child {\r\n        width: 120px;\r\n        color: #999999;\r\n    }\r\n    .table td:last-child {\r\n        padding-left: 15px;\r\n    }\r\n    .table .table-title {\r\n        background: #fafafa;\r\n    }\r\n\r\n    .table-detail {\r\n        width: 100%;\r\n        background: #fafafa;\r\n    }\r\n    .table-detail tr {\r\n        height: 45px;\r\n        text-align: center;\r\n    }\r\n    .table-detail .table-title {\r\n        color: #999999;\r\n    }\r\n    .table-detail .table-footer td {\r\n        border-top: 10px solid #ffffff;\r\n        padding: 0 20px;\r\n        text-align: right;\r\n    }\r\n    .header-title {\r\n        position: absolute;\r\n        right: 20px;\r\n        top: 38px;\r\n        font-size: 24px;\r\n        font-weight: 300;\r\n        color: #9c9c9c;\r\n    }\r\n</style>\r\n<div class=\"body\">\r\n    <#assign ods = orderDetails?eval/>\r\n    <div class=\"body-head\">\r\n        &nbsp;&nbsp;&nbsp;&nbsp;${tenantName!\'\'}(Tenant No. : ${tenantNo!\'\'}) has placed the order, and the payment time is ${paymentTime!\'\'}. Please timely provide perfect after-sales service for the paying tenants.\r\n    </div>\r\n    <table class=\"table-detail\">\r\n        <thead class=\"table-title\">\r\n        <tr>\r\n            <td><b>Name</b></td>\r\n            <td><b>Duration</b></td>\r\n            <td><b>UnitPrice</b></td>\r\n            <td><b>Quantity</b></td>\r\n            <td><b>Money(￥)</b></td>\r\n        </tr>\r\n        </thead>\r\n        <tbody>\r\n        <#list ods as orderDetail>\r\n        <tr>\r\n            <td>${orderDetail.saleName}</td>\r\n            <td>${orderDetail.duration}</td>\r\n            <td>${orderDetail.unitPrice}</td>\r\n            <td>${orderDetail.quantity}</td>\r\n            <td>${orderDetail.amount}</td>\r\n        </tr>\r\n        </#list>\r\n        </tbody>\r\n        <tfoot class=\"table-footer\">\r\n        <tr>\r\n            <td colspan=\"5\">\r\n                <span class=\"price\">${totalQuantity}</span> pieces in total,<span class=\"price\">￥${total}CNY</span> paid\r\n            </td>\r\n        </tr>\r\n        </tfoot>\r\n    </table>\r\n</div>\r\n\r\n', '订购通知(英文)', 'cloud', '2021-04-09 08:27:59', '2021-02-23 10:34:24', 0),
('cloudSubscribeSuccessEmail_zh_CN', '<style>\r\n    .content * {\r\n        margin: 0;\r\n        padding: 0;\r\n    }\r\n    .content {\r\n        width: 610px;\r\n        margin: 0 auto;\r\n        font-size: 16px;\r\n        position: relative;\r\n    }\r\n    .line {\r\n        width: 100%;\r\n        height: 5px;\r\n        background: linear-gradient(243deg, #665ba1 0%, #33b8e8 100%);\r\n    }\r\n    .header {\r\n        width: 100%;\r\n        height: 100px;\r\n        position: relative;\r\n        border-bottom: 2px solid #f5f5f5;\r\n        background: #fff;\r\n    }\r\n    .header .logo {\r\n        height: 50px;\r\n        width: 50px;\r\n        position: absolute;\r\n        left: 20px;\r\n        top: 50%;\r\n        margin-top: -25px;\r\n    }\r\n    .header .title {\r\n        font-size: 25px;\r\n        color: #191515;\r\n        font-weight: bold;\r\n        position: absolute;\r\n        left: 88px;\r\n        top: 50%;\r\n        margin-top: -15px;\r\n    }\r\n    .footer {\r\n        width: 100%;\r\n        height: 170px;\r\n        box-sizing: border-box;\r\n        background-color: #fafafa;\r\n        padding-top: 30px;\r\n        padding-left: 25px;\r\n    }\r\n    .footer .logo {\r\n        width: 35px;\r\n        height: 30px;\r\n    }\r\n    .footer .name {\r\n        margin-top: 26px;\r\n        color: #282828;\r\n        font-size: 16px;\r\n    }\r\n    .footer .tel {\r\n        margin-top: 10px;\r\n        color: #282828;\r\n        font-size: 16px;\r\n    }\r\n\r\n    .body {\r\n        padding: 25px 40px;\r\n        color: #282828;\r\n        background-color: #fff;\r\n        font-size: 16px;\r\n    }\r\n\r\n    .body-head {\r\n        margin: 25px 0;\r\n        color: #282828;\r\n        line-height: 22px;\r\n    }\r\n    .price {\r\n        color: #ff5c00;\r\n    }\r\n    .table {\r\n        width: 100%;\r\n    }\r\n    .table tr {\r\n        height: 45px;\r\n    }\r\n    .table td:first-child {\r\n        width: 120px;\r\n        color: #999999;\r\n    }\r\n    .table td:last-child {\r\n        padding-left: 15px;\r\n    }\r\n    .table .table-title {\r\n        background: #fafafa;\r\n    }\r\n\r\n    .table-detail {\r\n        width: 100%;\r\n        background: #fafafa;\r\n    }\r\n    .table-detail tr {\r\n        height: 45px;\r\n        text-align: center;\r\n    }\r\n    .table-detail .table-title {\r\n        color: #999999;\r\n    }\r\n    .table-detail .table-footer td {\r\n        border-top: 10px solid #ffffff;\r\n        padding: 0 20px;\r\n        text-align: right;\r\n    }\r\n    .header-title {\r\n        position: absolute;\r\n        right: 20px;\r\n        top: 38px;\r\n        font-size: 24px;\r\n        font-weight: 300;\r\n        color: #9c9c9c;\r\n    }\r\n</style>\r\n<div class=\"body\">\r\n    <#assign ods = orderDetails?eval/>\r\n    <div class=\"body-head\">\r\n        ${tenantName!\'\'}（租户编号：${tenantNo!\'\'}）已下单，支付时间：${paymentTime!\'\'}，请及时为付费租户提供完善的售后服务。\r\n    </div>\r\n    <table class=\"table-detail\">\r\n        <thead class=\"table-title\">\r\n        <tr>\r\n            <td><b>名称</b></td>\r\n            <td><b>时长</b></td>\r\n            <td><b>单价</b></td>\r\n            <td><b>数量</b></td>\r\n            <td><b>金额（元）</b></td>\r\n        </tr>\r\n        </thead>\r\n        <tbody>\r\n        <#list ods as orderDetail>\r\n        <tr>\r\n            <td>${orderDetail.saleName}</td>\r\n            <td>${orderDetail.duration}</td>\r\n            <td>${orderDetail.unitPrice}</td>\r\n            <td>${orderDetail.quantity}</td>\r\n            <td>${orderDetail.amount}</td>\r\n        </tr>\r\n        </#list>\r\n        </tbody>\r\n        <tfoot class=\"table-footer\">\r\n        <tr>\r\n            <td colspan=\"5\">\r\n                共<span class=\"price\">${totalQuantity}</span>件，实付款<span class=\"price\">￥${total}元</span>\r\n            </td>\r\n        </tr>\r\n        </tfoot>\r\n    </table>\r\n</div>', '订购通知(中文)', 'cloud', '2021-04-09 08:28:50', '2021-02-23 10:34:52', 0),
('cloudSubscribeSuccessEmail_zh_TW', '<style>\r\n    .content * {\r\n        margin: 0;\r\n        padding: 0;\r\n    }\r\n    .content {\r\n        width: 610px;\r\n        margin: 0 auto;\r\n        font-size: 16px;\r\n        position: relative;\r\n    }\r\n    .line {\r\n        width: 100%;\r\n        height: 5px;\r\n        background: linear-gradient(243deg, #665ba1 0%, #33b8e8 100%);\r\n    }\r\n    .header {\r\n        width: 100%;\r\n        height: 100px;\r\n        position: relative;\r\n        border-bottom: 2px solid #f5f5f5;\r\n        background: #fff;\r\n    }\r\n    .header .logo {\r\n        height: 50px;\r\n        width: 50px;\r\n        position: absolute;\r\n        left: 20px;\r\n        top: 50%;\r\n        margin-top: -25px;\r\n    }\r\n    .header .title {\r\n        font-size: 25px;\r\n        color: #191515;\r\n        font-weight: bold;\r\n        position: absolute;\r\n        left: 88px;\r\n        top: 50%;\r\n        margin-top: -15px;\r\n    }\r\n    .footer {\r\n        width: 100%;\r\n        height: 170px;\r\n        box-sizing: border-box;\r\n        background-color: #fafafa;\r\n        padding-top: 30px;\r\n        padding-left: 25px;\r\n    }\r\n    .footer .logo {\r\n        width: 35px;\r\n        height: 30px;\r\n    }\r\n    .footer .name {\r\n        margin-top: 26px;\r\n        color: #282828;\r\n        font-size: 16px;\r\n    }\r\n    .footer .tel {\r\n        margin-top: 10px;\r\n        color: #282828;\r\n        font-size: 16px;\r\n    }\r\n\r\n    .body {\r\n        padding: 25px 40px;\r\n        color: #282828;\r\n        background-color: #fff;\r\n        font-size: 16px;\r\n    }\r\n\r\n    .body-head {\r\n        margin: 25px 0;\r\n        color: #282828;\r\n        line-height: 22px;\r\n    }\r\n    .price {\r\n        color: #ff5c00;\r\n    }\r\n    .table {\r\n        width: 100%;\r\n    }\r\n    .table tr {\r\n        height: 45px;\r\n    }\r\n    .table td:first-child {\r\n        width: 120px;\r\n        color: #999999;\r\n    }\r\n    .table td:last-child {\r\n        padding-left: 15px;\r\n    }\r\n    .table .table-title {\r\n        background: #fafafa;\r\n    }\r\n\r\n    .table-detail {\r\n        width: 100%;\r\n        background: #fafafa;\r\n    }\r\n    .table-detail tr {\r\n        height: 45px;\r\n        text-align: center;\r\n    }\r\n    .table-detail .table-title {\r\n        color: #999999;\r\n    }\r\n    .table-detail .table-footer td {\r\n        border-top: 10px solid #ffffff;\r\n        padding: 0 20px;\r\n        text-align: right;\r\n    }\r\n    .header-title {\r\n        position: absolute;\r\n        right: 20px;\r\n        top: 38px;\r\n        font-size: 24px;\r\n        font-weight: 300;\r\n        color: #9c9c9c;\r\n    }\r\n</style>\r\n<div class=\"body\">\r\n    <#assign ods = orderDetails?eval/>\r\n    <div class=\"body-head\">\r\n        ${tenantName!\'\'}（租戶編號：${tenantNo!\'\'}）已下單，支付時間：${paymentTime!\'\'}，請及時為付費租戶提供完善的售後服務。\r\n    </div>\r\n    <table class=\"table-detail\">\r\n        <thead class=\"table-title\">\r\n        <tr>\r\n            <td><b>名稱</b></td>\r\n            <td><b>時長</b></td>\r\n            <td><b>單價</b></td>\r\n            <td><b>數量</b></td>\r\n            <td><b>金額（元）</b></td>\r\n        </tr>\r\n        </thead>\r\n        <tbody>\r\n        <#list ods as orderDetail>\r\n        <tr>\r\n            <td>${orderDetail.saleName}</td>\r\n            <td>${orderDetail.duration}</td>\r\n            <td>${orderDetail.unitPrice}</td>\r\n            <td>${orderDetail.quantity}</td>\r\n            <td>${orderDetail.amount}</td>\r\n        </tr>\r\n        </#list>\r\n        </tbody>\r\n        <tfoot class=\"table-footer\">\r\n        <tr>\r\n            <td colspan=\"5\">\r\n                共<span class=\"price\">${totalQuantity}</span>件，實付款<span class=\"price\">￥${total}元</span>\r\n            </td>\r\n        </tr>\r\n        </tfoot>\r\n    </table>\r\n</div>', '订购通知(繁体)', 'cloud', '2021-04-09 08:30:15', '2021-02-23 10:35:17', 0);
