UPDATE `mail_template` SET `content` = '<table style=\"margin:-16px auto;\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" width=\"600\" bgcolor=\"#FFFFFF\">\r\n <tr>\r\n <td colspan=\"2\">\r\n <table align=\"center\" class=\"banner\" cellspacing=\"0\" cellpadding=\"0\" width=\"600\"height=\"100\">\r\n <tr>\r\n <td colspan=\"2\" bgcolor=\"#F2F2F2\">\r\n <table cellpadding=\"0\" cellspacing=\"0\" style=\"margin:10px;\" width=\"580\"bgcolor=\"#FFFFFF\">\r\n <tr>\r\n <td align=\"left\" colspan=\"2\"style=\"font-size:14pt; line-height:30px;\">\r\n <#if emailType?? && (emailType == \"0-0\" || emailType == \"0-1\")>\r\n <span style=\"margin-left:10px;\">Application for service</span>\r\n <#elseif emailType?? && (emailType == \"1-0\" || emailType == \"1-1\" || emailType == \"2-0\" || emailType == \"2-1\")>\r\n <span style=\"margin-left:10px;\">Notification of the results of confirmation</span>\r\n <#elseif emailType?? && (emailType == \"3-0\" || emailType == \"3-1\")>\r\n <span style=\"margin-left:10px;\">Meeting service preparation notice</span>\r\n <#elseif emailType?? && (emailType == \"4-0\" || emailType == \"4-1\")>\r\n <span style=\"margin-left:10px;\">Application for meeting services cancelled</span>\r\n </#if>\r\n </td>\r\n </tr>\r\n <tr>\r\n <td align=\"left\" style=\"font-size:12pt; line-height:30px;\">\r\n <#if emailType?? && (emailType == \"0-0\" || emailType == \"0-1\")>\r\n <span style=\"margin-left:10px;\">Hi ${callEn!\'\'} :</span>\r\n <br/>\r\n <span class=\"k2\" style=\"margin-left:30px;\">You have received an application for meeting service. Please check it soon and give your feedback.</span>\r\n <#elseif emailType?? && (emailType == \"1-0\"|| emailType == \"1-1\")>\r\n <span style=\"margin-left:10px;\">Hi ${callEn!\'\'} :</span>\r\n <br/>\r\n <span class=\"k2\" style=\"margin-left:30px;\">Your application for meeting service has been approved.</span>\r\n <#elseif emailType?? && (emailType == \"2-0\" || emailType == \"2-1\")>\r\n <span style=\"margin-left:10px;\">Hi ${callEn!\'\'} :</span>\r\n <br/>\r\n <span class=\"k2\" style=\"margin-left:30px;\">Your application for meeting service has not been approved. Reason :${refuseRemark!\'\'}</span>\r\n <#elseif emailType?? && (emailType == \"3-0\"|| emailType == \"3-1\")>\r\n <span style=\"margin-left:10px;\">Hi ${callEn!\'\'} :</span>\r\n <br/>\r\n <span class=\"k2\" style=\"margin-left:30px;\">You have new conference services to prepare, please deal with them in time.</span>\r\n <#elseif emailType?? && (emailType == \"4-0\"|| emailType == \"4-1\")>\r\n <span style=\"margin-left:10px;\">Hi ${callEn!\'\'} :</span>\r\n <br/>\r\n <span class=\"k2\" style=\"margin-left:30px;\">The conference service has been cancelled. Please pay attention in time.</span>\r\n </#if>\r\n </td>\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n <tr valign=\"top\">\r\n <td colspan=\"2\" bgcolor=\"#F2F2F2\" style=\"padding: 0 10px 10px 10px;\">\r\n <table align=\"center\" valign=\"top\" cellspacing=\"0\" cellpadding=\"0\" width=\"580\"border=\"0\" bgcolor=\"#FFFFFF\">\r\n <tr valign=\"top\" align=\"center\">\r\n <td colspan=\"2\">\r\n <table cellpadding=\"10\" cellspacing=\"0\"style=\"border-bottom:solid 1px #f2f2f2; border-top:1px solid #f2f2f2; border-collapse:collapse;\"width=\"580\">\r\n <tr>\r\n <td align=\"right\" style=\"color:#b4b4b4;\" width=\"25%\">\r\n <span>Title&nbsp;:</span>\r\n </td>\r\n <td width=\"75%\" style=\"word-break:break-all\">${meetingName!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td align=\"right\" style=\"color:#b4b4b4;\" width=\"25%\">\r\n <span>Room&nbsp;:</span>\r\n </td>\r\n <td width=\"75%\">${roomName!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td align=\"right\" style=\"color:#b4b4b4;\" width=\"25%\">\r\n <span>Start&nbsp;:</span>\r\n </td>\r\n <td width=\"75%\">${meetingStartTime!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td align=\"right\" style=\"color:#b4b4b4;\" width=\"25%\">\r\n <span>End&nbsp;:</span>\r\n </td>\r\n <td width=\"75%\">${meetingEndTime!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td align=\"right\" style=\"color:#b4b4b4;\" width=\"25%\">\r\n <span>Host&nbsp;:</span>\r\n </td>\r\n <td width=\"75%\">${creatorUserEn!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td align=\"right\" style=\"color:#b4b4b4;\" valign=\"top\" width=\"25%\">\r\n <span>Service name&nbsp;:</span>\r\n </td>\r\n <td width=\"75%\">\r\n <table width=\"80%\">\r\n <tr style=\"background: #F8F6F5;\">\r\n <td align=\"left\" width=\"40%\" height=\"40px\"style=\"border-bottom: 1px solid #E4E4E4;\">Service</td>\r\n <#if emailType?? && (emailType==\"0-1\" || emailType==\"1-1\" || emailType==\"2-1\" || emailType==\"3-1\" || emailType == \"4-1\")>\r\n <td align=\"left\" width=\"20%\" height=\"40px\"style=\"border-bottom: 1px solid #E4E4E4;\">Level</td>\r\n </#if>\r\n <td align=\"left\" width=\"25%\" height=\"40px\"style=\"border-bottom: 1px solid #E4E4E4;\">Room</td>\r\n <td align=\"left\" width=\"15%\" height=\"40px\"style=\"border-bottom: 1px solid #E4E4E4;\">Number</td>\r\n </tr>\r\n ${svcApplies!\'\'}\r\n </table>\r\n </td>\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n</table>\r\n' WHERE name = 'smtSvcApply_en';
