UPDATE mail_template SET content = '<style>\r\n    .content * {\r\n        margin: 0;\r\n        padding: 0;\r\n    }\r\n    .content {\r\n        width: 610px;\r\n        margin: 0 auto;\r\n        font-size: 16px;\r\n        position: relative;\r\n    }\r\n    .line {\r\n        width: 100%;\r\n        height: 5px;\r\n        background: linear-gradient(243deg, #665ba1 0%, #33b8e8 100%);\r\n    }\r\n    .header {\r\n        width: 100%;\r\n        height: 100px;\r\n        position: relative;\r\n        border-bottom: 2px solid #f5f5f5;\r\n        background: #fff;\r\n    }\r\n    .header .logo {\r\n        height: 50px;\r\n        width: 50px;\r\n        position: absolute;\r\n        left: 20px;\r\n        top: 50%;\r\n        margin-top: -25px;\r\n    }\r\n    .header .title {\r\n        font-size: 25px;\r\n        color: #191515;\r\n        font-weight: bold;\r\n        position: absolute;\r\n        left: 88px;\r\n        top: 50%;\r\n        margin-top: -15px;\r\n    }\r\n    .footer {\r\n        width: 100%;\r\n        height: 170px;\r\n        box-sizing: border-box;\r\n        background-color: #fafafa;\r\n        padding-top: 30px;\r\n        padding-left: 25px;\r\n    }\r\n    .footer .logo {\r\n        width: 35px;\r\n        height: 30px;\r\n    }\r\n    .footer .name {\r\n        margin-top: 26px;\r\n        color: #282828;\r\n        font-size: 16px;\r\n    }\r\n    .footer .tel {\r\n        margin-top: 10px;\r\n        color: #282828;\r\n        font-size: 16px;\r\n    }\r\n\r\n    .body {\r\n        padding: 25px 40px;\r\n        color: #282828;\r\n        background-color: #fff;\r\n        font-size: 16px;\r\n    }\r\n\r\n    .body-head {\r\n        margin: 25px 0;\r\n        color: #282828;\r\n        line-height: 22px;\r\n    }\r\n    .price {\r\n        color: #ff5c00;\r\n    }\r\n	.redRemind {\r\n        color: red;\r\n    }\r\n    .table {\r\n        width: 100%;\r\n    }\r\n    .table tr {\r\n        height: 45px;\r\n    }\r\n    .table td:first-child {\r\n        width: 200px;\r\n        color: #999999;\r\n    }\r\n    .table td:last-child {\r\n        padding-left: 15px;\r\n    }\r\n    .table .table-title {\r\n        background: #fafafa;\r\n    }\r\n\r\n    .table-detail {\r\n        width: 100%;\r\n        background: #fafafa;\r\n    }\r\n    .table-detail tr {\r\n        height: 45px;\r\n        text-align: center;\r\n    }\r\n    .table-detail .table-title {\r\n        color: #999999;\r\n    }\r\n    .table-detail .table-footer td {\r\n        border-top: 10px solid #ffffff;\r\n        padding: 0 20px;\r\n        text-align: right;\r\n    }\r\n    .header-title {\r\n        position: absolute;\r\n        right: 20px;\r\n        top: 38px;\r\n        font-size: 24px;\r\n        font-weight: 300;\r\n        color: #9c9c9c;\r\n    }\r\n</style>\r\n<div class=\"body\">\r\n    <div class=\"body-head\">\r\n        &nbsp;&nbsp;&nbsp;&nbsp;User ${userName!\'\'}(E-mail:${userEmail!\'\'}) of ${tenantName!\'\'}(tenantNo:${tenantNo!\'\'}) applies for invoicing the order ${orderNo!\'\'}。<span class=\"redRemind\">${extendInfo!\'\'}</span>\r\n    </div>\r\n    <table class=\"table\">\r\n        <thead class=\"table-title\">\r\n        <tr>\r\n            <td colspan=\"2\">Invoice Details</td>\r\n        </tr>\r\n        </thead>\r\n        <tbody>\r\n        <tr>\r\n            <td align=\"right\">Headup Type:</td>\r\n            <td align=\"left\">${headUpType!\'\'}</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Invoice Type:</td>\r\n            <td align=\"left\">${invoiceType!\'\'}</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Company Name:</td>\r\n            <td align=\"left\">${companyName!\'\'}</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Taxpayer Identification No:</td>\r\n            <td align=\"left\">${companyTaxNumber!\'\'}</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Registered address:</td>\r\n            <td align=\"left\">${companyAddress!\'\'}</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Company phone:</td>\r\n            <td align=\"left\">${workPhone!\'\'}</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Account bank:</td>\r\n            <td align=\"left\">${depositBank!\'\'}</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Account number:</td>\r\n            <td align=\"left\">${bankAccount!\'\'}</td>\r\n        </tr>\r\n		 <tr>\r\n            <td align=\"right\">Contact:</td>\r\n            <td align=\"left\">${contactPerson!\'\'}</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Phone:</td>\r\n            <td align=\"left\">${contactNumber!\'\'}</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Email:</td>\r\n            <td align=\"left\">${contactMail!\'\'}</td>\r\n        </tr>\r\n		 <tr>\r\n            <td align=\"right\">Invoice Amount:</td>\r\n            <td align=\"left\"><span class=\"price\">¥${invoiceAmount!\'\'}</span> CNY</td>\r\n        </tr>\r\n        <tr>\r\n            <td align=\"right\">Application Time:</td>\r\n            <td align=\"left\">${applicationTime!\'\'}</td>\r\n        </tr>\r\n        </tbody>\r\n    </table>\r\n</div>'
WHERE category = 'cloud' AND name ='cloudTenantApplyInvoice_en';

