-- 变更邀请-员工 模板修改
update `mail_template` set `content` = '<table width=\"573\" align=\"center\"\r\n style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;margin-top: 9px;;\">\r\n <tr>\r\n <td>\r\n <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\r\n <tr>\r\n <td style=\"color:#333333;font-size: 20px;\">访问变更通知</td>\r\n <td style=\"width: 100px;\"></td>\r\n </tr>\r\n <tr>\r\n <td style=\"color:#333333; padding-top:15px;font-size: 16px;\">${employeeName!\'\'}，您好！</td>\r\n <td style=\"width: 100px;text-align: center;height: 32px;font-size: 16px;\">\r\n </td>\r\n </tr>\r\n <tr>\r\n <td style=\"color:#333333; padding-top:10px; padding-bottom:15px;line-height: 2;font-size: 16px;\">\r\n 您的邀约已经变更成功，请注意及时接待。</td>\r\n <td style=\"height: 32px;width: 88px;text-align: center;padding-top: 10px;font-size: 16px;\"\r\n valign=\"top\">\r\n </td>\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n\r\n <#list visitors as visitor>\r\n <tr style=\"border-top: 1px solid #eaeaea;\">\r\n <td>\r\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%;padding-top:25px; \">\r\n 访客姓名: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-top: 25px;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitorName!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 访客公司: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitorCompany!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 访问时间: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitTime!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%;padding-bottom: 25px;\">\r\n 访问事由: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-bottom: 25px;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitPurpose!\'\'}</td>\r\n\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n </#list>\r\n <!-- 有会议信息才显示 -->\r\n <#if isShowMeetingInfo == \'1\'>\r\n <tr style=\"border-top: 1px solid #eaeaea;\">\r\n <td>\r\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 会议主题: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${topic!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 会议室: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${roomName!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 开始时间: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${smtStartTime!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 结束时间: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${smtEndTime!\'\'}</td>\r\n </tr>\r\n <#if meetingNo?? && meetingNo != \'\'>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 会议ID: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${meetingNo!\'\'}</td>\r\n </tr>\r\n </#if>\r\n <#if password?? && password != \'\'>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 参会密码: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${password!\'\'}</td>\r\n </tr>\r\n </#if>\r\n <#if url?? && url != \'\'>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 邀请链接: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n <a href=${url!\'\'} target=\"_blank\" style=\"margin-left:19px;display:-webkit-box;-webkit-line-clamp:2;\r\n -webkit-box-orient:vertical;overflow: hidden;word-break: break-word;\">${url!\'\'}</a></td>\r\n </tr>\r\n </#if>\r\n </table>\r\n </td>\r\n </tr>\r\n </#if>\r\n <tr style=\"border-top: 1px solid #eaeaea;\">\r\n <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\r\n <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">祝您度过愉快的一天!</p>\r\n </td>\r\n </tr>\r\n</table>' where `name` = 'vstConfirmedEditEmp_zh_CN';
update `mail_template` set `content` = '<table width=\"573\" align=\"center\"\r\n style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;margin-top: 9px;;\">\r\n <tr>\r\n <td>\r\n <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\r\n <tr>\r\n <td style=\"color:#333333;font-size: 20px;\">訪問變更通知</td>\r\n <td style=\"width: 100px;\"></td>\r\n </tr>\r\n <tr>\r\n <td style=\"color:#333333; padding-top:15px;font-size: 16px;\">${employeeName!\'\'}，您好！</td>\r\n <td style=\"width: 100px;text-align: center;height: 32px;font-size: 16px;\">\r\n </td>\r\n </tr>\r\n <tr>\r\n <td style=\"color:#333333; padding-top:10px; padding-bottom:15px;line-height: 2;font-size: 16px;\">\r\n 您的邀約已經變更成功，請注意及時接待。</td>\r\n <td style=\"height: 32px;width: 88px;text-align: center;padding-top: 10px;font-size: 16px;\"\r\n valign=\"top\">\r\n </td>\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n\r\n <#list visitors as visitor>\r\n <tr style=\"border-top: 1px solid #eaeaea;\">\r\n <td>\r\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%;padding-top:25px; \">\r\n 訪客姓名: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-top: 25px;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitorName!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 訪客公司: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitorCompany!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 訪問時間: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitTime!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%;padding-bottom: 25px;\">\r\n 訪問事由: </td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-bottom: 25px;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitPurpose!\'\'}</td>\r\n\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n </#list>\r\n <!-- 有会议信息才显示 -->\r\n <#if isShowMeetingInfo == \'1\'>\r\n <tr style=\"border-top: 1px solid #eaeaea;\">\r\n <td>\r\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 會議主題 :</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${topic!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 會議室 :</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${roomName!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 開始時間 :</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${smtStartTime!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 結束時間 :</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${smtEndTime!\'\'}</td>\r\n </tr>\r\n <#if meetingNo?? && meetingNo != \'\'>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 會議ID :</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${meetingNo!\'\'}</td>\r\n </tr>\r\n </#if>\r\n <#if password?? && password != \'\'>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 參會密碼 :</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${password!\'\'}</td>\r\n </tr>\r\n </#if>\r\n <#if url?? && url != \'\'>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n 邀請鏈接 :</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n <a href=${url!\'\'} target=\"_blank\" style=\"margin-left:19px;display:-webkit-box;-webkit-line-clamp:2;\r\n -webkit-box-orient:vertical;overflow: hidden;word-break: break-word;\">${url!\'\'}</a></td>\r\n </tr>\r\n </#if>\r\n </table>\r\n </td>\r\n </tr>\r\n </#if>\r\n <tr style=\"border-top: 1px solid #eaeaea;\">\r\n <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\r\n <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">祝您度過愉快的一天!</p>\r\n </td>\r\n </tr>\r\n</table>' where `name` = 'vstConfirmedEditEmp_zh_TW';
update `mail_template` set `content` = '<table width=\"573\" align=\"center\"\r\n style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;margin-top: 9px;;\">\r\n <tr>\r\n <td>\r\n <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\r\n <tr>\r\n <td style=\"color:#333333;font-size: 20px;\">Visit Modification</td>\r\n <td style=\"width: 100px;\"></td>\r\n </tr>\r\n <tr style=\"line-height: 2;\">\r\n <td style=\"color:#333333; padding-top:25px;font-size: 16px;\">Dear ${employeeName!\'\'}:<br>Your visit invitation has been successfully modified. Please pay attention to timely reception.</td>\r\n <td style=\"width: 100px;font-size: 16px;\"></td>\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n\r\n <#list visitors as visitor>\r\n <tr style=\"border-top: 1px solid #eaeaea;\">\r\n <td>\r\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%;padding-top:25px; \">\r\n Visitor name:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-top: 25px;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitorName!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n Visitor company:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitorCompany!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n Visit time:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitTime!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%;padding-bottom: 25px;\">\r\n Purpose:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-bottom: 25px;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${visitor.visitPurpose!\'\'}</td>\r\n\r\n </tr>\r\n </table>\r\n </td>\r\n </tr>\r\n </#list>\r\n <!-- 有会议信息才显示 -->\r\n <#if isShowMeetingInfo == \'1\'>\r\n <tr style=\"border-top: 1px solid #eaeaea;\">\r\n <td>\r\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n Title:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${topic!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n Room:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${roomName!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n Start:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${smtStartTime!\'\'}</td>\r\n </tr>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n End:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${smtEndTime!\'\'}</td>\r\n </tr>\r\n <#if meetingNo?? && meetingNo != \'\'>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n Meeting ID:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${meetingNo!\'\'}</td>\r\n </tr>\r\n </#if>\r\n <#if password?? && password != \'\'>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n Password:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n &nbsp;&nbsp;&nbsp;&nbsp;${password!\'\'}</td>\r\n </tr>\r\n </#if>\r\n <#if url?? && url != \'\'>\r\n <tr>\r\n <td width=\"150\" height=\"50\"\r\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\r\n Invitation URL:</td>\r\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\r\n <a href=${url!\'\'} target=\"_blank\" style=\"margin-left:19px;display:-webkit-box;-webkit-line-clamp:2;\r\n -webkit-box-orient:vertical;overflow: hidden;word-break: break-word;\">${url!\'\'}</a></td>\r\n </tr>\r\n </#if>\r\n </table>\r\n </td>\r\n </tr>\r\n </#if>\r\n <tr style=\"border-top: 1px solid #eaeaea;\">\r\n <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\r\n <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">We wish you a pleasant day!</p>\r\n </td>\r\n </tr>\r\n</table>' where `name` = 'vstConfirmedEditEmp_en';
-- SQX模板修改
update `mail_template` set `content` = '<table width=\"573\" align=\"center\"\n style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;margin-top: 9px;;\">\n <tr>\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\n <tr>\n <td style=\"color:#333333;font-size: 20px;\">访问邀请确认函</td>\n <td style=\"width: 100px;\"></td>\n </tr>\n <tr>\n <td style=\"color:#333333; padding-top:15px;font-size: 16px;\">${visitorName!\'\'}，您好！</td>\n <td style=\"width: 100px;text-align: center;height: 32px;\">\n </td>\n </tr>\n <tr>\n <td style=\"color:#333333; padding-top:10px; padding-bottom:15px;line-height: 2;font-size: 16px;\">\n ${employeeName!\'\'}诚邀您访问${locationName!\'\'}，具体访问细节如下: </td>\n <td style=\"height: 32px;width: 88px;text-align: center;padding-top: 10px;\"\n valign=\"top\">\n </td>\n </tr>\n </table>\n </td>\n </tr>\n\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 邀请人: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeeName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 访问时间: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${visitTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 联系电话: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeePhone!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 公司地址: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n ${companyAddress!\'\'}</td>\n </tr>\n <!-- 人脸识别开关开启才显示 -->\n <#if faceSwitch == \'1\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 上传照片:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n <a href=\"${faceUploadLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">点击上传人脸识别照片</a></td>\n </tr>\n </#if>\n \n \n   <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'1\'>\n<tr>\n	<td width=\"150\" height=\"50\"\n		style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n		签到信息:</td>\n	<td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n		<a href=\"${qrCodeLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">点击查看</a></td>\n</tr>\n </#if>\n <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'0\'>\n <tr style=\"padding-top: -30px;\">\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; padding-bottom: 36px; text-align: right; line-height:120%\">\n 签到二维码</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding: 0 0 15px 20px\">\n 请保存二维码或打印此函，到达后可扫描二维码或输入邀请码（${qrCode!\'\'}）完成签到。<br>\n </td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 44px;padding-bottom: 10px;\">\n <img src=\"${qrCodeLink!\'\'}\" alt=\"InvitationCode\"\n style=\"margin-top: 1px; margin-left: -24px;width: 150px;height: 150px;\"/>\n </td>\n </tr>\n </#if>\n \n </table>\n </td>\n </tr>\n <!-- 有会议信息才显示 -->\n <#if isShowMeetingInfo == \'1\'>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 会议主题: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${topic!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 会议室: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${roomName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 开始时间: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtStartTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 结束时间: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtEndTime!\'\'}</td>\n </tr>\n <#if meetingNo?? && meetingNo != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 会议ID: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${meetingNo!\'\'}</td>\n </tr>\n </#if>\n <#if password?? && password != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 参会密码: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${password!\'\'}</td>\n </tr>\n </#if>\n <#if url?? && url != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 邀请链接: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n <a href=${url!\'\'} target=\"_blank\" style=\"margin-left:19px;display:-webkit-box;-webkit-line-clamp:2;\n -webkit-box-orient:vertical;overflow: hidden;word-break: break-word;\">${url!\'\'}</a></td>\n </tr>\n </#if>\n </table>\n </td>\n </tr>\n </#if>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\n <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">祝您度过愉快的一天!</p>\n </td>\n </tr>\n</table>' WHERE `name` = 'vstConfirmedVisitor_zh_CN';
update `mail_template` set `content` = '<table width=\"573\" align=\"center\"\n style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;margin-top: 9px;;\">\n <tr>\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\n <tr>\n <td style=\"color:#333333;font-size: 20px;\">訪問邀請確認函</td>\n <td style=\"width: 100px;\"></td>\n </tr>\n <tr>\n <td style=\"color:#333333; padding-top:15px;font-size: 16px;\">${visitorName!\'\'}，您好！</td>\n <td style=\"width: 100px;text-align: center;height: 32px;\">\n </td>\n </tr>\n <tr>\n <td style=\"color:#333333; padding-top:10px; padding-bottom:15px;line-height: 2;font-size: 16px;\">\n ${employeeName!\'\'}誠邀您訪問${locationName!\'\'}，具體訪問細節如下：</td>\n <td style=\"height: 32px;width: 88px;text-align: center;padding-top: 10px;\"\n valign=\"top\">\n </td>\n </tr>\n </table>\n </td>\n </tr>\n\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 邀請人: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeeName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 預約時間: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${visitTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 聯系電話: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeePhone!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 公司地址: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n ${companyAddress!\'\'}</td>\n </tr>\n <!-- 人脸识别开关开启才显示 -->\n <#if faceSwitch == \'1\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 上傳照片:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n <a href=\"${faceUploadLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">點擊上傳人臉識別照片</a></td>\n </tr>\n </#if>\n \n   <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'1\'>\n<tr>\n	<td width=\"150\" height=\"50\"\n		style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n		簽到資訊:</td>\n	<td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n		<a href=\"${qrCodeLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">點擊查看</a></td>\n</tr>\n </#if>\n <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'0\'>\n <tr style=\"padding-top: -30px;\">\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; padding-bottom: 36px; text-align: right; line-height:120%\">\n 簽到二維碼</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding: 0 0 15px 20px\">\n 請保存二維碼或打印此函，到達後可掃描二維碼或輸入邀請碼（${qrCode!\'\'}）完成簽到。<br>\n </td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 44px;padding-bottom: 10px;\">\n <img src=\"${qrCodeLink!\'\'}\" alt=\"InvitationCode\"\n style=\"margin-top: 1px; margin-left: -24px;width: 150px;height: 150px;\"/>\n </td>\n </tr>\n </#if>\n </table>\n </td>\n </tr>\n <!-- 有会议信息才显示 -->\n <#if isShowMeetingInfo == \'1\'>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 會議主題 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${topic!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 會議室 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${roomName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 開始時間 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtStartTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 結束時間 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtEndTime!\'\'}</td>\n </tr>\n <#if meetingNo?? && meetingNo != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 會議ID :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${meetingNo!\'\'}</td>\n </tr>\n </#if>\n <#if password?? && password != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 參會密碼 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${password!\'\'}</td>\n </tr>\n </#if>\n <#if url?? && url != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 邀請鏈接 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n <a href=${url!\'\'} target=\"_blank\" style=\"margin-left:19px;display:-webkit-box;-webkit-line-clamp:2;\n -webkit-box-orient:vertical;overflow: hidden;word-break: break-word;\">${url!\'\'}</a></td>\n </tr>\n </#if>\n </table>\n </td>\n </tr>\n </#if>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\n <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">祝您度過愉快的一天!</p>\n </td>\n </tr>\n</table>' WHERE `name` = 'vstConfirmedVisitor_zh_TW';
update `mail_template` set `content` = '<table width=\"573\" align=\"center\"\n style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;margin-top: 9px;;\">\n <tr>\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\n <tr>\n <td style=\"color:#333333;font-size: 20px;\">Visit Confirmation</td>\n <td style=\"width: 100px;\"></td>\n </tr>\n <tr style=\"line-height: 2;\">\n <td style=\"color:#333333; padding-top:25px;font-size: 16px;\">Dear ${visitorName!\'\'}:<br>We are very pleased to inform you of the visit details as below.</td>\n <td style=\"width: 100px;\"></td>\n </tr>\n </table>\n </td>\n </tr>\n\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Host Employee:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeeName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Visit Time:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${visitTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Mobile:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeePhone!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Address:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n ${companyAddress!\'\'}</td>\n </tr>\n <!-- 人脸识别开关开启才显示 -->\n <#if faceSwitch == \'1\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Upload Photo:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n <a href=\"${faceUploadLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">Click to upload face recognition photos</a></td>\n </tr>\n </#if>\n \n <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'1\'>\n<tr>\n	<td width=\"150\" height=\"50\"\n		style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n		Check-in<br>Information:</td>\n	<td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n		<a href=\"${qrCodeLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">Click to view</a></td>\n</tr>\n </#if>\n <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'0\'>\n <tr style=\"padding-top: -30px;\">\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; padding-bottom: 36px; text-align: right; line-height:120%\">\n QR Code:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding: 0 0 15px 20px\">\n Please save this QR code on your phone or print it out after your arrival to sign in.（${qrCode!\'\'}）<br>\n </td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 44px;padding-bottom: 10px;\">\n <img src=\"${qrCodeLink!\'\'}\" alt=\"InvitationCode\"\n style=\"margin-top: 1px; margin-left: -24px;width: 150px;height: 150px;\"/>\n </td>\n </tr>\n </#if>\n \n </table>\n </td>\n </tr>\n <!-- 有会议信息才显示 -->\n <#if isShowMeetingInfo == \'1\'>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Title:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${topic!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Room:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${roomName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Start:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtStartTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n End:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtEndTime!\'\'}</td>\n </tr>\n <#if meetingNo?? && meetingNo != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Meeting ID:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${meetingNo!\'\'}</td>\n </tr>\n </#if>\n <#if password?? && password != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Password:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${password!\'\'}</td>\n </tr>\n </#if>\n <#if url?? && url != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Invitation URL:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n <a href=${url!\'\'} target=\"_blank\" style=\"margin-left:19px;display:-webkit-box;-webkit-line-clamp:2;\n -webkit-box-orient:vertical;overflow: hidden;word-break: break-word;\">${url!\'\'}</a></td>\n </tr>\n </#if>\n </table>\n </td>\n </tr>\n </#if>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\n <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">We wish you a pleasant day!</p>\n </td>\n </tr>\n</table>' WHERE `name` = 'vstConfirmedVisitor_en';
update `mail_template` set `content` = '<table width=\"573\" align=\"center\"\n style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;margin-top: 9px;;\">\n <tr>\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\n <tr>\n <td style=\"color:#333333;font-size: 20px;\">访问变更通知</td>\n <td style=\"width: 100px;\"></td>\n </tr>\n <tr>\n <td style=\"color:#333333; padding-top:15px;font-size: 16px;\">${visitorName!\'\'}，您好！</td>\n <td style=\"width: 100px;text-align: center;height: 32px;\">\n </td>\n </tr>\n <tr>\n <td style=\"color:#333333; padding-top:10px; padding-bottom:15px;line-height: 2;font-size: 16px;\">\n 您【${oldVisitTime!\'\'}】的邀约已被变更，新的访问信息如下：</td>\n <td style=\"height: 32px;width: 88px;text-align: center;padding-top: 10px;font-size: 16px;\"\n valign=\"top\">\n </td>\n </tr>\n </table>\n </td>\n </tr>\n\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 邀请人: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeeName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 预约时间: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${visitTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 联系电话: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeePhone!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 公司地址: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n ${companyAddress!\'\'}</td>\n </tr>\n <!-- 人脸识别开关开启才显示 -->\n <#if faceSwitch == \'1\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 上传照片:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n <a href=\"${faceUploadLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">点击上传人脸识别照片</a></td>\n </tr>\n </#if>\n \n \n  <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'1\'>\n<tr>\n	<td width=\"150\" height=\"50\"\n		style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n		签到信息:</td>\n	<td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n		<a href=\"${qrCodeLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">点击查看</a></td>\n</tr>\n </#if>\n <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'0\'>\n <tr style=\"padding-top: -30px;\">\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; padding-bottom: 36px; text-align: right; line-height:120%\">\n 签到二维码</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding: 0 0 15px 20px\">\n 请保存二维码或打印此函，到达后可扫描二维码或输入邀请码（${qrCode!\'\'}）完成签到。<br>\n </td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 44px;padding-bottom: 10px;\">\n <img src=\"${qrCodeLink!\'\'}\" alt=\"InvitationCode\"\n style=\"margin-top: 1px; margin-left: -24px;width: 150px;height: 150px;\"/>\n </td>\n </tr>\n </#if>\n \n </table>\n </td>\n </tr>\n <!-- 有会议信息才显示 -->\n <#if isShowMeetingInfo == \'1\'>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 会议主题: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${topic!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 会议室: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${roomName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 开始时间: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtStartTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 结束时间: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtEndTime!\'\'}</td>\n </tr>\n <#if meetingNo?? && meetingNo != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 会议ID: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${meetingNo!\'\'}</td>\n </tr>\n </#if>\n <#if password?? && password != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 参会密码: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${password!\'\'}</td>\n </tr>\n </#if>\n <#if url?? && url != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 邀请链接: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n <a href=${url!\'\'} target=\"_blank\" style=\"margin-left:19px;display:-webkit-box;-webkit-line-clamp:2;\n -webkit-box-orient:vertical;overflow: hidden;word-break: break-word;\">${url!\'\'}</a></td>\n </tr>\n </#if>\n </table>\n </td>\n </tr>\n </#if>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\n <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">祝您度过愉快的一天!</p>\n </td>\n </tr>\n</table>' WHERE `name` = 'vstConfirmedEditVisitor_zh_CN';
update `mail_template` set `content` = '<table width=\"573\" align=\"center\"\n style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;margin-top: 9px;;\">\n <tr>\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\n <tr>\n <td style=\"color:#333333;font-size: 20px;\">訪問變更通知</td>\n <td style=\"width: 100px;\"></td>\n </tr>\n <tr>\n <td style=\"color:#333333; padding-top:15px;font-size: 16px;\">${visitorName!\'\'}，您好！</td>\n <td style=\"width: 100px;text-align: center;height: 32px;\">\n </td>\n </tr>\n <tr>\n <td style=\"color:#333333; padding-top:10px; padding-bottom:15px;line-height: 2;font-size: 16px;\">\n 您【${oldVisitTime!\'\'}】的邀約已被變更，新的訪問資訊如下：</td>\n <td style=\"height: 32px;width: 88px;text-align: center;padding-top: 10px;font-size: 16px;\"\n valign=\"top\">\n </td>\n </tr>\n </table>\n </td>\n </tr>\n\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 邀請人: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeeName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 預約時間: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${visitTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 聯系電話: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeePhone!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 公司地址: </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n ${companyAddress!\'\'}</td>\n </tr>\n <!-- 人脸识别开关开启才显示 -->\n <#if faceSwitch == \'1\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 上傳照片:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n <a href=\"${faceUploadLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">點擊上傳人臉識別照片</a></td>\n </tr>\n </#if>\n\n \n   <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'1\'>\n<tr>\n	<td width=\"150\" height=\"50\"\n		style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n		簽到資訊:</td>\n	<td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n		<a href=\"${qrCodeLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">點擊查看</a></td>\n</tr>\n </#if>\n <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'0\'>\n <tr style=\"padding-top: -30px;\">\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; padding-bottom: 36px; text-align: right; line-height:120%\">\n 簽到二維碼</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding: 0 0 15px 20px\">\n 請保存二維碼或打印此函，到達後可掃描二維碼或輸入邀請碼（${qrCode!\'\'}）完成簽到。<br>\n </td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 44px;padding-bottom: 10px;\">\n <img src=\"${qrCodeLink!\'\'}\" alt=\"InvitationCode\"\n style=\"margin-top: 1px; margin-left: -24px;width: 150px;height: 150px;\"/>\n </td>\n </tr>\n </#if>\n \n </table>\n </td>\n </tr>\n <!-- 有会议信息才显示 -->\n <#if isShowMeetingInfo == \'1\'>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 會議主題 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${topic!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 會議室 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${roomName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 開始時間 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtStartTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 結束時間 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtEndTime!\'\'}</td>\n </tr>\n <#if meetingNo?? && meetingNo != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 會議ID :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${meetingNo!\'\'}</td>\n </tr>\n </#if>\n <#if password?? && password != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 參會密碼 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${password!\'\'}</td>\n </tr>\n </#if>\n <#if url?? && url != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n 邀請鏈接 :</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n <a href=${url!\'\'} target=\"_blank\" style=\"margin-left:19px;display:-webkit-box;-webkit-line-clamp:2;\n -webkit-box-orient:vertical;overflow: hidden;word-break: break-word;\">${url!\'\'}</a></td>\n </tr>\n </#if>\n </table>\n </td>\n </tr>\n </#if>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\n <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">祝您度過愉快的一天!</p>\n </td>\n </tr>\n</table>' WHERE `name` = 'vstConfirmedEditVisitor_zh_TW';
update `mail_template` set `content` = '<table width=\"573\" align=\"center\"\n style=\"border: 1px solid #eaeaea;border-collapse: collapse; border-spacing: 0;margin-top: 9px;;\">\n <tr>\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\" style=\"padding-left:18px;padding-top: 21px;\">\n <tr>\n <td style=\"color:#333333;font-size: 20px;\">Visit Modification</td>\n <td style=\"width: 100px;\"></td>\n </tr>\n <tr style=\"line-height: 2;\">\n <td style=\"color:#333333; padding-top:25px;font-size: 16px;\">Dear ${visitorName!\'\'}:<br>Your invitation on ${oldVisitTime!\'\'} has been modified as below.</td>\n <td style=\"width: 100px;font-size: 16px;\"></td>\n </tr>\n </table>\n </td>\n </tr>\n\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Host employee:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeeName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Visit time:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${visitTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Mobile:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${employeePhone!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Address:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n ${companyAddress!\'\'}</td>\n </tr>\n <!-- 人脸识别开关开启才显示 -->\n <#if faceSwitch == \'1\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Upload Photo:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n <a href=\"${faceUploadLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">Click to upload face recognition photos</a></td>\n </tr>\n </#if>\n \n <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'1\'>\n<tr>\n	<td width=\"150\" height=\"50\"\n		style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n		Check-in<br>Information:</td>\n	<td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 20px;\">\n		<a href=\"${qrCodeLink!\'\'}\" style=\"color:#0D75CD;font-size: 16px\">Click to view</a></td>\n</tr>\n </#if>\n <!-- sqx二维码开关 -->\n <#if sqxSwitch == \'0\'>\n <tr style=\"padding-top: -30px;\">\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; padding-bottom: 36px; text-align: right; line-height:120%\">\n QR Code:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding: 0 0 15px 20px\">\n Please save this QR code on your phone or print it out after your arrival to sign in.（${qrCode!\'\'}）<br>\n </td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n </td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;padding-left: 44px;padding-bottom: 10px;\">\n <img src=\"${qrCodeLink!\'\'}\" alt=\"InvitationCode\"\n style=\"margin-top: 1px; margin-left: -24px;width: 150px;height: 150px;\"/>\n </td>\n </tr>\n </#if>\n\n \n </table>\n </td>\n </tr>\n <!-- 有会议信息才显示 -->\n <#if isShowMeetingInfo == \'1\'>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td>\n <table width=\"100%\" bgcolor=\"#FFFFFF\">\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Title:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${topic!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Room:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${roomName!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Start:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtStartTime!\'\'}</td>\n </tr>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n End:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${smtEndTime!\'\'}</td>\n </tr>\n <#if meetingNo?? && meetingNo != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Meeting ID:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${meetingNo!\'\'}</td>\n </tr>\n </#if>\n <#if password?? && password != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Password:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n &nbsp;&nbsp;&nbsp;&nbsp;${password!\'\'}</td>\n </tr>\n </#if>\n <#if url?? && url != \'\'>\n <tr>\n <td width=\"150\" height=\"50\"\n style=\"font-size: 16px; color:#999999; text-align: right; line-height:120%\">\n Invitation URL:</td>\n <td style=\"font-size: 16px; color:#333333; text-align:left;\">\n <a href=${url!\'\'} target=\"_blank\" style=\"margin-left:19px;display:-webkit-box;-webkit-line-clamp:2;\n -webkit-box-orient:vertical;overflow: hidden;word-break: break-word;\">${url!\'\'}</a></td>\n </tr>\n </#if>\n </table>\n </td>\n </tr>\n </#if>\n <tr style=\"border-top: 1px solid #eaeaea;\">\n <td style=\"font-size: 18px;\" bgcolor=\"#ffffff\">\n <p style=\"margin-top: 15px;margin-right: 0;margin-bottom: 15px;margin-left: 55px;\">We wish you a pleasant day!</p>\n </td>\n </tr>\n</table>' WHERE `name` = 'vstConfirmedEditVisitor_en';